 logger.info("Attempting to save character: " + (character != null ? character.getName() : "null"));

 // Input validation
 if (character == null || filename == null || filename.trim().isEmpty()) {
 logger.warning("Save failed: null/empty character or filename");
 System.out.println("Error saving: invalid input!");
 return false;
 }

 // Sanitize filename
 String safeFilename = InputValidator.sanitizeFilename(filename.trim());
 if (safeFilename.isEmpty()) {
 logger.warning("Save failed: invalid filename after sanitization");
 System.out.println("Error saving: invalid filename!");
 return false;
 }

 try {
 // Create saves directory if needed
 createSaveDirectory();

 // Convert character to JSON
 JsonObject characterData = serializeCharacter(character);

 // Write to file with exception handling
 Path filePath = Paths.get(SAVE_DIR, safeFilename + FILE_EXT);
 Files.writeString(filePath, gson.toJson(characterData));

 logger.info("Character saved successfully: " + character.getName());
 System.out.println("Character saved!");
 return true;

 } catch (IOException e) {
 logger.severe("File write error: " + e.getMessage());
 ExceptionHandler.handleSaveLoadError(e);
 return false;
 } catch (Exception e) {
 logger.severe("Unexpected error during save: " + e.getMessage());
 ExceptionHandler.handleSaveLoadError(e);
 return false;
 }
 }

 /**
 * Load character from JSON file with exception shielding
 */
 public static Character loadCharacter(String filename) {
 logger.info("Attempting to load character from: " + filename);

 // Input validation
 if (filename == null || filename.trim().isEmpty()) {
 logger.warning("Load failed: invalid filename");
 System.out.println("Invalid filename!");
 return null;
 }

 // Sanitize filename
 String safeFilename = InputValidator.sanitizeFilename(filename.trim());
 if (safeFilename.isEmpty()) {
 logger.warning("Load failed: invalid filename after sanitization");
 System.out.println("Invalid filename!");
 return null;
 }

 try {
 // Read JSON file
 Path filePath = Paths.get(SAVE_DIR, safeFilename + FILE_EXT);
 String jsonContent = Files.readString(filePath);

 // Deserialize character
 return deserializeCharacter(jsonContent);

 } catch (NoSuchFileException e) {
 logger.warning("Save file not found: " + safeFilename);
 System.out.println("Save file not found!");
 return null;
 } catch (IOException e) {
 logger.severe("File read error: " + e.getMessage());
 ExceptionHandler.handleSaveLoadError(e);
 return null;
 } catch (Exception e) {
 logger.severe("Error recreating character: " + e.getMessage());
 ExceptionHandler.handleSaveLoadError(e);
 return null;
 }
 }

 /**
 * Convert character to JSON object
 */
 private static JsonObject serializeCharacter(Character character) {
 JsonObject json = new JsonObject();

 // Save basic info
 json.addProperty("name", character.getName());
 json.addProperty("type", character.getClass().getSimpleName());
 json.addProperty("health", character.getHealth());
 json.addProperty("maxHealth", character.getMaxHealth());
 json.addProperty("stamina", character.getStamina());
 json.addProperty("maxStamina", character.getMaxStamina());
 json.addProperty("damage", character.getBaseDamage());
 json.addProperty("money", character.getMoney());
 json.addProperty("level", character.getLevel());

 // Save mage-specific data
 if (character instanceof Mage) {
 Mage mage = (Mage) character;
 json.addProperty("mana", mage.getMana());
 json.addProperty("maxMana", mage.getMaxMana());
 logger.info("Saved mage-specific data for: " + character.getName());
 }

 return json;
 }

 /**
 * Create character from JSON data
 */
 private static Character deserializeCharacter(String jsonContent) {
 JsonObject json = gson.fromJson(jsonContent, JsonObject.class);

 // Extract basic info
 String name = json.get("name").getAsString();
 String type = json.get("type").getAsString();

 // Create character
 CharacterFactory factory = new CharacterFactory();
 String charType = type.equals("Warrior") ? "warrior" : "mage";
 Character character = factory.createCharacter(charType, name);

 if (character == null) {
 logger.warning("Character factory returned null");
 return null;
 }

 // Set character properties
 // Note: This would require additional setters in Character interface
 // For now, we're just creating a basic character

 logger.info("Character loaded successfully: " + name);
 System.out.println("Character loaded: " + name);
 return character;
 }

 /**
 * Create save directory if it doesn't exist
 */
 private static void createSaveDirectory() throws IOException {
 Path dirPath = Paths.get(SAVE_DIR);
 if (!Files.exists(dirPath)) {
 Files.createDirectory(dirPath);
 logger.info("Save directory created");
 }
 }

 /**
 * Check if save exists with exception handling
 */
 public static boolean saveExists(String filename) {
 if (filename == null || filename.trim().isEmpty()) {
 logger.warning("saveExists called with invalid filename");
 return false;
 }

 try {
 String safeFilename = InputValidator.sanitizeFilename(filename.trim());
 Path filePath = Paths.get(SAVE_DIR, safeFilename + FILE_EXT);
 boolean exists = Files.exists(filePath);
 logger.info("Save file check for " + safeFilename + ": " + exists);
 return exists;
 } catch (Exception e) {
 logger.warning("Error checking save file existence: " + e.getMessage());
 return false;
 }
 }

 /**
 * List save files with exception handling
 */
 public static String[] listSaveFiles() {
 logger.info("Listing save files");

 try {
 Path dirPath = Paths.get(SAVE_DIR);
 if (!Files.exists(dirPath)) {
 logger.info("Save directory doesn't exist");
 return new String[0];
 }

 // Get all JSON files in directory
 return Files.list(dirPath)
 .filter(path -> path.toString().endsWith(FILE_EXT))
 .map(path -> path.getFileName().toString().replace(FILE_EXT, ""))
 .toArray(String[]::new);

 } catch (Exception e) {
 logger.severe("Error listing save files: " + e.getMessage());
 return new String[0];
 }
 }

 /**
 * Delete save file with exception handling
 */
 public static boolean deleteSave(String filename) {
 logger.info("Attempting to delete save: " + filename);

 if (filename == null || filename.trim().isEmpty()) {
 logger.warning("Delete called with invalid filename");
 return false;
 }

 try {
 String safeFilename = InputValidator.sanitizeFilename(filename.trim());
 Path filePath = Paths.get(SAVE_DIR, safeFilename + FILE_EXT);

 if (Files.exists(filePath)) {
 Files.delete(filePath);
 logger.info("Save file deleted successfully: " + safeFilename);
 System.out.println("Save deleted!");
 return true;
 } else {
 logger.info("Save file not found for deletion: " + safeFilename);
 System.out.println("Save not found!");
 return false;
 }
 } catch (Exception e) {
 logger.severe("Error deleting save file: " + e.getMessage());
 System.out.println("Delete failed!");
 return false;
 }
 }
}
