package com.exam.project.io;

import com.exam.project.factory.Character;
import com.exam.project.factory.CharacterFactory;
import com.exam.project.factory.Warrior;
import com.exam.project.factory.Mage;
import com.exam.project.logger.GameLogger;
import com.exam.project.security.InputValidator;

import java.io.*;
import java.util.Properties;
import java.util.logging.Logger;

/**
 * Simple save/load for characters
 * Basic I/O implementation for exam requirements
 */
public class CharacterManagement {

 private static final String SAVE_DIR = "saves";
 private static final String FILE_EXT = ".save";
 private static final Logger logger = GameLogger.getLogger();

 /**
 * Save character to file
 */
 public static boolean saveCharacter(Character character, String filename) {
 logger.info("Attempting to save character: " + (character != null ? character.getName() : "null"));

 if (character == null || filename == null) {
 logger.warning("Save failed: null character or filename");
 System.out.println("Error saving!");
 return false;
 }

 // Sanitize filename
 String safeFilename = InputValidator.sanitizeFilename(filename.trim());
 if (safeFilename.isEmpty()) {
 logger.warning("Save failed: invalid filename");
 System.out.println("Error saving: invalid filename!");
 return false;
 }

 try {
 // Create saves directory if needed
 File dir = new File(SAVE_DIR);
 if (!dir.exists()) {
 boolean created = dir.mkdir();
 logger.info("Save directory created: " + created);
 }

 Properties props = new Properties();

 // Save basic info
 props.setProperty("name", character.getName());
 props.setProperty("type", character.getClass().getSimpleName());
 props.setProperty("health", String.valueOf(character.getHealth()));
 props.setProperty("maxHealth", String.valueOf(character.getMaxHealth()));
 props.setProperty("stamina", String.valueOf(character.getStamina()));
 props.setProperty("maxStamina", String.valueOf(character.getMaxStamina()));
 props.setProperty("damage", String.valueOf(character.getBaseDamage()));
 props.setProperty("money", String.valueOf(character.getMoney()));
 props.setProperty("level", String.valueOf(character.getLevel()));

 // Save mage mana if needed
 if (character instanceof Mage) {
 Mage mage = (Mage) character;
 props.setProperty("mana", String.valueOf(mage.getMana()));
 props.setProperty("maxMana", String.valueOf(mage.getMaxMana()));
 logger.info("Saved mage-specific data for: " + character.getName());
 }

 // Write to file
 try (FileOutputStream out = new FileOutputStream(SAVE_DIR + "/" + safeFilename + FILE_EXT)) {
 props.store(out, "Character Save");
 logger.info("Character saved successfully: " + character.getName());
 System.out.println("Character saved!");
 return true;
 }
 } catch (IOException e) {
 logger.severe("File write error: " + e.getMessage());
 System.out.println("Save failed!");
 return false;
 } catch (Exception e) {
 logger.severe("Unexpected error during save: " + e.getMessage());
 System.out.println("Save failed!");
 return false;
 }
 }

 /**
 * Load character from file
 */
 public static Character loadCharacter(String filename) {
 logger.info("Attempting to load character from: " + filename);

 if (filename == null || filename.trim().isEmpty()) {
 logger.warning("Load failed: invalid filename");
 System.out.println("Invalid filename!");
 return null;
 }

 // Sanitize filename
 String safeFilename = InputValidator.sanitizeFilename(filename.trim());
 if (safeFilename.isEmpty()) {
 logger.warning("Load failed: invalid filename");
 System.out.println("Invalid filename!");
 return null;
 }

 Properties props = new Properties();

 try (FileInputStream in = new FileInputStream(SAVE_DIR + "/" + safeFilename + FILE_EXT)) {
 props.load(in);
 logger.info("File loaded successfully: " + safeFilename);
 } catch (FileNotFoundException e) {
 logger.warning("Save file not found: " + safeFilename);
 System.out.println("Save file not found!");
 return null;
 } catch (IOException e) {
 logger.severe("File read error: " + e.getMessage());
 System.out.println("Load failed!");
 return null;
 }

 try {
 String name = props.getProperty("name");
 String type = props.getProperty("type");

 if (name == null || type == null) {
 logger.warning("Corrupted save file - missing name or type");
 System.out.println("Bad save file!");
 return null;
 }

 // Create character
 CharacterFactory factory = new CharacterFactory();
 String charType = type.equals("Warrior") ? "warrior" : "mage";
 Character character = factory.createCharacter(charType, name);

 if (character != null) {
 logger.info("Character loaded successfully: " + name);
 System.out.println("Character loaded: " + name);
 } else {
 logger.warning("Character factory returned null");
 System.out.println("Failed to create character!");
 }

 return character;
 } catch (Exception e) {
 logger.severe("Error recreating character: " + e.getMessage());
 System.out.println("Load failed!");
 return null;
 }
 }

 /**
 * Check if save exists
 */
 public static boolean saveExists(String filename) {
 if (filename == null || filename.trim().isEmpty()) {
 logger.warning("saveExists called with invalid filename");
 return false;
 }

 try {
 String safeFilename = InputValidator.sanitizeFilename(filename.trim());
 File file = new File(SAVE_DIR + "/" + safeFilename + FILE_EXT);
 boolean exists = file.exists();
 logger.info("Save file check for " + safeFilename + ": " + exists);
 return exists;
 } catch (Exception e) {
 logger.warning("Error checking save file existence: " + e.getMessage());
 return false;
 }
 }

 /**
 * List save files
 */
 public static String[] listSaveFiles() {
 logger.info("Listing save files");

 try {
 File dir = new File(SAVE_DIR);
 if (!dir.exists()) {
 logger.info("Save directory doesn't exist");
 return new String[0];
 }

 File[] files = dir.listFiles((d, name) -> name.endsWith(FILE_EXT));
 if (files == null) {
 logger.warning("Could not list files in save directory");
 return new String[0];
 }

 String[] names = new String[files.length];
 for (int i = 0; i < files.length; i++) {
 names[i] = files[i].getName().replace(FILE_EXT, "");
 }

 logger.info("Found " + names.length + " save files");
 return names;
 } catch (Exception e) {
 logger.severe("Error listing save files: " + e.getMessage());
 return new String[0];
 }
 }

 /**
 * Delete save file
 */
 public static boolean deleteSave(String filename) {
 logger.info("Attempting to delete save: " + filename);

 if (filename == null || filename.trim().isEmpty()) {
 logger.warning("Delete called with invalid filename");
 return false;
 }

 try {
 String safeFilename = InputValidator.sanitizeFilename(filename.trim());
 File file = new File(SAVE_DIR + "/" + safeFilename + FILE_EXT);

 if (file.exists()) {
 boolean deleted = file.delete();
 if (deleted) {
 logger.info("Save file deleted successfully: " + safeFilename);
 System.out.println("Save deleted!");
 } else {
 logger.warning("Failed to delete save file: " + safeFilename);
 System.out.println("Delete failed!");
 }
 return deleted;
 } else {
 logger.info("Save file not found for deletion: " + safeFilename);
 System.out.println("Save not found!");
 return false;
 }
 } catch (Exception e) {
 logger.severe("Error deleting save file: " + e.getMessage());
 System.out.println("Delete failed!");
 return false;
 }
 }
}
```
</augment_code_snippet>

Questa versione è più semplice perché:

1. Usa `Properties` invece di JSON (più semplice, non richiede dipendenze esterne)
2. Utilizza l'API I/O standard di Java invece di NIO.2
3. Mantiene una struttura semplice ma con sanitizzazione degli input
4. Usa try-with-resources per gestire automaticamente la chiusura degli stream
5. Mantiene la gestione degli errori essenziale senza delegare a classi esterne
6. Conserva i log per il debugging

Ho aggiunto la sanitizzazione dei nomi file e la gestione dei file vuoti, che sono elementi di sicurezza essenziali anche in una versione semplificata.
