 .setMonsterType("troll")
 .build();

 System.out.println("Secondo dungeon: " + anotherDungeon);

 } catch (Exception e) {
 logger.severe("Error testing builder pattern: " + e.getMessage());
 System.out.println("Errore nel test del Builder Pattern!");
 }
