````java path=Java_Exam_Project/src/main/java/com/exam/project/builder/Dungeon.java mode=EDIT
package com.exam.project.builder;

import com.exam.project.logger.GameLogger;
import com.exam.project.factoryMonster.AbstractMonster;
import com.exam.project.factoryMonster.MonsterFactory;
import java.util.logging.Logger;
import java.util.ArrayList;
import java.util.List;

/**
 * Dungeon - Oggetto costruito dal Builder Pattern
 *
 * Versione semplificata: 1 stanza, 1 tipo di mostro
 */
public class Dungeon {

 private static final Logger logger = GameLogger.getLogger();
 private static final MonsterFactory monsterFactory = new MonsterFactory();

 // Attributi immutabili
 private final String name;
 private final String description;
 private final int goldReward;
 private final String monsterType;
 private final int numberOfRooms;

 /**
 * Costruttore package-private - solo il builder può creare Dungeon
 */
 Dungeon(String name, String description, int goldReward, String monsterType) {
 try {
 this.name = (name != null) ? name : "Unknown Dungeon";
 this.description = (description != null) ? description : "";
 this.goldReward = Math.max(0, goldReward);
 this.monsterType = (monsterType != null) ? monsterType : "goblin";

 // Determina il numero di stanze in base al tipo
 this.numberOfRooms = this.monsterType.equals("troll") ? 3 : 2;

 logger.info("Dungeon created: " + this.name + " with " + this.numberOfRooms + " rooms");
 } catch (Exception e) {
 logger.severe("Error creating Dungeon: " + e.getMessage());
 throw new RuntimeException("Failed to create Dungeon", e);
 }
 }

 // Getters pubblici
 public String getName() {
 return name;
 }

 public String getDescription() {
 return description;
 }

 public int getGoldReward() {
 return goldReward;
 }

 public String getMonsterType() {
 return monsterType;
 }

 // Metodi per compatibilità con codice esistente
 public int getNumberOfRooms() {
 return numberOfRooms;
 }

 public int getExperienceReward() {
 return goldReward / 2; // Exp = metà dell'oro
 }

 public java.util.List<String> getMonsterTypes() {
 return java.util.Collections.singletonList(monsterType);
 }

 public int getMonstersPerRoom() {
 // Più mostri nella palude dei troll
 return monsterType.equals("troll") ? 2 : 1;
 }

 public int getBaseDifficulty() {
 return monsterType.equals("troll") ? 2 : 1;
 }

 /**
 * Crea i mostri per questo dungeon usando MonsterFactory
 */
 public List<AbstractMonster> createMonstersForRoom() {
 logger.info("Creating monsters for room in " + name);
 List<AbstractMonster> monsters = new ArrayList<>();

 try {
 int count = getMonstersPerRoom();
 int difficulty = getBaseDifficulty();

 for (int i = 0; i < count; i++) {
 AbstractMonster monster = monsterFactory.createMonster(monsterType, difficulty);
 if (monster != null) {
 monsters.add(monster);
 logger.info("Created " + monsterType + " for " + name);
 }
 }
 } catch (Exception e) {
 logger.warning("Error creating monsters: " + e.getMessage());
 }

 return monsters;
 }

 /**
 * Descrizione completa per compatibilità
 */
 public String getFullDescription() {
 try {
 StringBuilder desc = new StringBuilder();
 desc.append(String.format("=== %s ===%n", getName().toUpperCase()));

 if (!description.isEmpty()) {
 desc.append(description).append("\n\n");
 }

 desc.append("Stanze: ").append(numberOfRooms).append("\n");
 desc.append(String.format("Ricompense: %d oro, %d esperienza%n",
 getGoldReward(), getExperienceReward()));
 desc.append("Nemico: ").append(monsterType);

 return desc.toString();
 } catch (Exception e) {
 logger.severe("Error creating description: " + e.getMessage());
 return "Error: Cannot display dungeon information";
 }
 }

 @Override
 public String toString() {
 try {
 return String.format("%s (%d stanze) - %d oro, %s",
 getName(), getNumberOfRooms(), getGoldReward(), monsterType);
 } catch (Exception e) {
 return "Dungeon [Error]";
 }
 }
}
````

````java path=Java_Exam_Project/src/main/java/com/exam/project/builder/DungeonFactory.java mode=EDIT
package com.exam.project.builder;

import com.exam.project.logger.GameLogger;
import java.util.logging.Logger;

/**
 * DungeonFactory - Usa il Builder Pattern per creare i 2 dungeon
 *
 * Versione semplificata: usa ConcreteDungeonBuilder
 */
public class DungeonFactory {

 private static final Logger logger = GameLogger.getLogger();
 private static final DungeonBuilder builder = new ConcreteDungeonBuilder();

 /**
 * Crea Goblin Cave usando Builder Pattern
 */
 public static Dungeon createGoblinCave() {
 try {
 logger.info("Creating Goblin Cave dungeon");
 return builder.reset()
 .setName("Goblin Cave")
 .setDescription("Una grotta oscura piena di goblin. Perfetta per principianti.")
 .setGoldReward(100)
 .setMonsterType("goblin")
 .build();

 } catch (Exception e) {
 logger.severe("Error creating Goblin Cave: " + e.getMessage());
 // Fallback di emergenza
 return builder.reset()
 .setName("Emergency Cave")
 .setMonsterType("goblin")
 .build();
 }
 }

 /**
 * Crea Swamp of Trolls usando Builder Pattern
 */
 public static Dungeon createSwampOfTrolls() {
 try {
 logger.info("Creating Swamp of Trolls dungeon");
 return builder.reset()
 .setName("Swamp of Trolls")
 .setDescription("Una palude pericolosa dove si nascondono potenti troll.")
 .setGoldReward(200)
 .setMonsterType("troll")
 .build();

 } catch (Exception e) {
 logger.severe("Error creating Swamp of Trolls: " + e.getMessage());
 // Fallback di emergenza
 return builder.reset()
 .setName("Emergency Swamp")
 .setMonsterType("troll")
 .build();
 }
 }

 /**
 * Mostra i 2 dungeon disponibili
 */
 public static void showAvailableDungeons() {
 try {
 logger.info("Showing available dungeons");
 System.out.println("\n=== DUNGEON DISPONIBILI ===\n");

 Dungeon goblinCave = createGoblinCave();
 System.out.println("1. " + goblinCave.getName());
 System.out.println(" " + goblinCave.toString());
 System.out.println(" Difficoltà: ★☆☆ (Principiante)");

 System.out.println();

 Dungeon trollSwamp = createSwampOfTrolls();
 System.out.println("2. " + trollSwamp.getName());
 System.out.println(" " + trollSwamp.toString());
 System.out.println(" Difficoltà: ★★★ (Avanzato)");

 System.out.println("\nScegli in base al tuo livello di esperienza!");

 } catch (Exception e) {
 logger.severe("Error showing dungeons: " + e.getMessage());
 System.out.println("Errore nel mostrare i dungeon.");
 }
 }

 /**
 * Crea dungeon per scelta - SOLO 1 o 2
 */
 public static Dungeon createDungeonByChoice(int choice) {
 try {
 logger.info("Creating dungeon by choice: " + choice);
 switch (choice) {
 case 1:
 System.out.println("\nHai scelto la Goblin Cave!");
 return createGoblinCave();

 case 2:
 System.out.println("\nHai scelto la Swamp of Trolls!");
 return createSwampOfTrolls();

 default:
 logger.warning("Invalid dungeon choice: " + choice + ", defaulting to Goblin Cave");
 System.out.println("Scelta non valida! Usando Goblin Cave.");
 return createGoblinCave();
 }
 } catch (Exception e) {
 logger.severe("Error creating dungeon by choice: " + e.getMessage());
 return createGoblinCave();
 }
 }

 /**
 * Metodo per testare il Builder Pattern
 */
 public static void testBuilderPattern() {
 try {
 logger.info("Testing Builder Pattern");
 System.out.println("\n=== TEST BUILDER PATTERN ===");

 // Test creazione custom
 DungeonBuilder testBuilder = new ConcreteDungeonBuilder();

 Dungeon customDungeon = testBuilder
 .setName("Test Dungeon")
 .setDescription("Dungeon di test")
 .setGoldReward(75)
 .setMonsterType("goblin")
 .build();

 System.out.println("Custom dungeon creato: " + customDungeon);

 // Test reset e riutilizzo
 Dungeon anotherDungeon = testBuilder.reset()
 .setName("Another Test")
 .setGoldReward(150)
 .setMonsterType("troll")
 .build();

 System.out.println("Secondo dungeon: " + anotherDungeon);

 } catch (Exception e) {
 logger.severe("Error testing builder pattern: " + e.getMessage());
 System.out.println("Errore nel test del Builder Pattern!");
 }
 }
}
