````java path=Java_Exam_Project/src/main/java/com/exam/project/composite/MenuItem.java mode=EDIT
package com.exam.project.composite;

import java.util.ArrayList;
import java.util.List;

/**
 * MenuItem - Leaf nel Composite Pattern
 * Rappresenta un'azione singola nel menu
 */
public class MenuItem implements MenuComponent {

 private String name;
 private MenuAction action;
 private List<MenuComponent> children; // Per supportare il composite pattern

 /**
 * Costruttore per un item di menu semplice
 */
 public MenuItem(String name, MenuAction action) {
 this.name = name;
 this.action = action;
 this.children = new ArrayList<>(); // Inizializza la lista anche se normalmente vuota
 }

 @Override
 public void display() {
 System.out.println("- " + name);
 }

 @Override
 public void execute() {
 if (action != null) {
 action.execute();
 }
 }

 @Override
 public String getName() {
 return name;
 }

 /**
 * Implementazione di add per supportare il Composite Pattern
 * Anche se MenuItem è una foglia, implementiamo questo metodo
 * per mantenere l'uniformità dell'interfaccia
 */
 @Override
 public void add(MenuComponent component) {
 // In un MenuItem semplice, questo potrebbe trasformarlo in un sottomenu
 children.add(component);
 }

 /**
 * Implementazione di remove per supportare il Composite Pattern
 */
 @Override
 public void remove(MenuComponent component) {
 children.remove(component);
 }

 /**
 * Verifica se questo MenuItem ha figli (è diventato un composite)
 */
 public boolean hasChildren() {
 return !children.isEmpty();
 }

 /**
 * Ottiene i figli di questo MenuItem
 */
 public List<MenuComponent> getChildren() {
 return new ArrayList<>(children);
 }
}
````

````java path=Java_Exam_Project/src/main/java/com/exam/project/iterator/Item.java mode=EDIT
package com.exam.project.iterator;

import java.util.Iterator;
import java.util.NoSuchElementException;

/**
 * Item - Base class for all game items
 * Part of the inventory system using Collections and Generics
 */
public class Item implements Iterable<Item> {
 private final String name;
 private final ItemType type;
 private final int value;
 private final int statBonus;

 /**
 * ItemType enumeration - defines different item categories
 */
 public enum ItemType {
 WEAPON("Weapon", true),
 ARMOR("Armor", true),
 POTION("Potion", false),
 MISC("Miscellaneous", false);

 private final String displayName;
 private final boolean equippable;

 ItemType(String displayName, boolean equippable) {
 this.displayName = displayName;
 this.equippable = equippable;
 }

 public String getDisplayName() {
 return displayName;
 }

 public boolean isEquippable() {
 return equippable;
 }
 }

 /**
 * Constructor for creating a new item
 */
 public Item(String name, ItemType type, int value, int statBonus) {
 this.name = name;
 this.type = type;
 this.value = value;
 this.statBonus = statBonus;
 }

 /**
 * Get the item's name
 */
 public String getName() {
 return name;
 }

 /**
 * Get the item's type
 */
 public ItemType getType() {
 return type;
 }

 /**
 * Get the item's gold value
 */
 public int getValue() {
 return value;
 }

 /**
 * Get the item's stat bonus
 */
 public int getStatBonus() {
 return statBonus;
 }

 /**
 * Check if the item is equippable
 */
 public boolean isEquippable() {
 return type.isEquippable();
 }

 @Override
 public String toString() {
 String bonus = statBonus > 0 ? String.format(" (+%d)", statBonus) : "";
 return String.format("%s [%s] Value: %d gold%s",
 name, type.getDisplayName(), value, bonus);
 }

 @Override
 public boolean equals(Object obj) {
 if (this == obj) return true;
 if (obj == null || getClass() != obj.getClass()) return false;

 Item item = (Item) obj;
 return name.equals(item.name) && type == item.type;
 }

 @Override
 public int hashCode() {
 int result = name.hashCode();
 result = 31 * result + type.hashCode();
 return result;
 }

 /**
 * Implementazione dell'interfaccia Iterable
 * Permette di iterare su questo item (self-iterator)
 */
 @Override
 public Iterator<Item> iterator() {
 return new ItemIterator(this);
 }

 /**
 * Implementazione dell'Iterator Pattern
 * Questo iteratore permette di iterare su un singolo item
 * (utile per uniformità con container di item)
 */
 private class ItemIterator implements Iterator<Item> {
 private final Item item;
 private boolean hasNext = true;

 public ItemIterator(Item item) {
 this.item = item;
 }

 @Override
 public boolean hasNext() {
 return hasNext;
 }

 @Override
 public Item next() {
 if (!hasNext) {
 throw new NoSuchElementException("No more items");
 }
 hasNext = false;
 return item;
 }
 }
}
````

````java path=Java_Exam_Project/src/main/java/com/exam/project/observer/GameUIObserver.java mode=EDIT
package com.exam.project.observer;

import com.exam.project.factory.Character;
import com.exam.project.logger.GameLogger;
import java.util.logging.Logger;
import java.util.List;
import java.util.ArrayList;

/**
 * GameUIObserver - Concrete implementation of StaminaObserver
 *
 * This class represents the "UI" part of our game that wants to know
 * when stamina changes happen. In a real game, this might update
 * health bars, show notifications, or trigger visual effects.
 *
 * Observer Pattern allows this UI class to react to stamina changes
 * without the Character classes needing to know about UI details.
 */
public class GameUIObserver implements StaminaObserver {

 private static final Logger logger = GameLogger.getLogger();
 private boolean showRecoveryMessages = true;
 private List<Character> observedCharacters = new ArrayList<>();

 /**
 * Constructor
 */
 public GameUIObserver() {
 logger.info("GameUIObserver created");
 }

 /**
 * Called when a character's stamina changes
 */
 @Override
 public void onStaminaChanged(Character character, int oldStamina, int newStamina) {
 try {
 if (!observedCharacters.contains(character)) {
 observedCharacters.add(character);
 }

 int diff = newStamina - oldStamina;
 String change = diff > 0 ? "increased" : "decreased";

 logger.info(String.format("%s's stamina %s: %d -> %d",
 character.getName(), change, oldStamina, newStamina));

 // In a real game, this would update UI elements
 if (Math.abs(diff) > 5) {
 System.out.println("[UI] " + character.getName() + "'s stamina " + 
 change + " by " + Math.abs(diff));
 }
 } catch (Exception e) {
 logger.warning("Error in onStaminaChanged: " + e.getMessage());
 }
 }

 /**
 * Called when automatic stamina recovery occurs
 */
 @Override
 public void onStaminaRecovered(Character character, int recoveredAmount) {
 try {
 if (!observedCharacters.contains(character)) {
 observedCharacters.add(character);
 }

 if (showRecoveryMessages && recoveredAmount > 0) {
 // Only show recovery messages if enabled and something was recovered
 System.out.println(" " + character.getName() +
 " naturally recovers " + recoveredAmount + " stamina");
 }

 // Always log for debugging
 logger.info(String.format("Auto-recovery: %s gained %d stamina",
 character.getName(), recoveredAmount));

 } catch (Exception e) {
 logger.warning("Error in onStaminaRecovered: " + e.getMessage());
 }
 }

 /**
 * Enable or disable recovery messages
 */
 public void setShowRecoveryMessages(boolean show) {
 this.showRecoveryMessages = show;
 logger.info("Recovery messages " + (show ? "enabled" : "disabled"));
 }

 /**
 * Notifica tutti i personaggi osservati di un evento
 * Implementazione del pattern Observer
 */
 public void notifyObservers(String message) {
 logger.info("Notifying all observers: " + message);

 for (Character character : observedCharacters) {
 try {
 System.out.println("[UI Notification] " + character.getName() + ": " + message);
 } catch (Exception e) {
 logger.warning("Error notifying character: " + e.getMessage());
 }
 }
 }

 /**
 * Notifica un personaggio specifico
 */
 public void notifyObserver(Character character, String message) {
 if (character != null && observedCharacters.contains(character)) {
 logger.info("Notifying observer " + character.getName() + ": " + message);
 System.out.println("[UI Notification] " + character.getName() + ": " + message);
 }
 }
}
````

