<augment_code_snippet path="Java_Exam_Project/src/main/java/com/exam/project/iterator/Inventory.java" mode="EDIT">
```java
package com.exam.project.iterator;

import com.exam.project.logger.GameLogger;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.Iterator;
import java.util.List;
import java.util.Map;
import java.util.NoSuchElementException;
import java.util.logging.Logger;

/**
 * Inventory - Manages a character's items
 * Implements Iterator Pattern for easy traversal
 */
public class Inventory implements Iterable<Item> {
 private static final Logger logger = GameLogger.getLogger();

 private List<Item> items;
 private Map<Item.ItemType, Item> equippedItems;
 private int maxCapacity;

 /**
 * Constructor
 */
 public Inventory(int maxCapacity) {
 this.items = new ArrayList<>();
 this.equippedItems = new HashMap<>();
 this.maxCapacity = Math.max(1, maxCapacity);
 logger.info("Created inventory with capacity: " + this.maxCapacity);
 }

 /**
 * Add an item to inventory
 */
 public boolean addItem(Item item) {
 if (item == null) {
 logger.warning("Attempted to add null item to inventory");
 return false;
 }

 if (items.size() >= maxCapacity) {
 logger.info("Cannot add item: inventory full");
 return false;
 }

 items.add(item);
 logger.info("Added item to inventory: " + item.getName());
 return true;
 }

 /**
 * Remove an item from inventory
 */
 public boolean removeItem(Item item) {
 if (item == null) {
 return false;
 }

 // If equipped, unequip first
 if (equippedItems.containsValue(item)) {
 unequipItem(item);
 }

 boolean removed = items.remove(item);
 if (removed) {
 logger.info("Removed item from inventory: " + item.getName());
 }
 return removed;
 }

 /**
 * Equip an item
 */
 public boolean equipItem(Item item) {
 if (item == null || !items.contains(item) || !item.isEquippable()) {
 return false;
 }

 Item.ItemType type = item.getType();

 // Unequip any existing item of same type
 if (equippedItems.containsKey(type)) {
 unequipItem(equippedItems.get(type));
 }

 equippedItems.put(type, item);
 logger.info("Equipped item: " + item.getName());
 return true;
 }

 /**
 * Unequip an item
 */
 public boolean unequipItem(Item item) {
 if (item == null) {
 return false;
 }

 boolean removed = equippedItems.values().remove(item);
 if (removed) {
 logger.info("Unequipped item: " + item.getName());
 }
 return removed;
 }

 /**
 * Get all items of a specific type
 */
 public List<Item> getItemsByType(Item.ItemType type) {
 List<Item> result = new ArrayList<>();

 for (Item item : items) {
 if (item.getType() == type) {
 result.add(item);
 }
 }

 return result;
 }

 /**
 * Get all equipped items
 */
 public List<Item> getEquippedItems() {
 return new ArrayList<>(equippedItems.values());
 }

 /**
 * Check if an item is equipped
 */
 public boolean isEquipped(Item item) {
 return equippedItems.containsValue(item);
 }

 /**
 * Get inventory size
 */
 public int getSize() {
 return items.size();
 }

 /**
 * Get max capacity
 */
 public int getMaxCapacity() {
 return maxCapacity;
 }

 /**
 * Get all items
 */
 public List<Item> getAllItems() {
 return new ArrayList<>(items);
 }

 /**
 * Calculates total value of all items
 */
 public int getTotalValue() {
 return items.stream()
 .mapToInt(Item::getValue)
 .sum();
 }

 /**
 * Gets total stat bonus from equipped items
 */
 public int getTotalStatBonus() {
 return equippedItems.values().stream()
 .mapToInt(Item::getStatBonus)
 .sum();
 }

 /**
 * Sells an item (removes it and returns its value)
 */
 public int sellItem(Item item) {
 if (item == null || !items.contains(item)) {
 return 0;
 }

 int value = item.getValue();
 removeItem(item);
 System.out.println("Sold " + item.getName() + " for " + value + " gold");
 return value;
 }

 /**
 * Iterator Pattern implementation
 * Allows for-each loops over inventory items
 */
 @Override
 public Iterator<Item> iterator() {
 return new InventoryIterator();
 }

 /**
 * Custom iterator implementation for Inventory
 * Part of the Iterator Pattern
 */
 private class InventoryIterator implements Iterator<Item> {
 private int currentIndex = 0;

 @Override
 public boolean hasNext() {
 return currentIndex < items.size();
 }

 @Override
 public Item next() {
 if (!hasNext()) {
 throw new NoSuchElementException("No more items in inventory");
 }
 return items.get(currentIndex++);
 }
 }
}
```
