````java path=Java_Exam_Project/src/main/java/com/exam/project/composite/GameMenu.java mode=EDIT
package com.exam.project.composite;

import com.exam.project.security.InputValidator;
import com.exam.project.logger.GameLogger;

import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;
import java.util.logging.Logger;

/**
 * GameMenu - Composite nel Composite Pattern
 * 
 * Questa classe rappresenta un menu che può contenere altri menu o azioni.
 * Implementa il Composite Pattern per creare strutture di menu annidate.
 */
public class GameMenu implements MenuComponent {

 private static final Logger logger = GameLogger.getLogger();
 private String name;
 private List<MenuComponent> menuItems;
 private Scanner scanner;

 /**
 * Costruttore per un menu di gioco
 */
 public GameMenu(String name) {
 this.name = name;
 this.menuItems = new ArrayList<>();
 this.scanner = new Scanner(System.in);
 logger.info("Created menu: " + name);
 }

 /**
 * Aggiunge un componente a questo menu (Composite Pattern)
 */
 @Override
 public void add(MenuComponent menuItem) {
 if (menuItem != null) {
 menuItems.add(menuItem);
 logger.info("Added item '" + menuItem.getName() + "' to menu '" + name + "'");
 }
 }

 /**
 * Rimuove un componente da questo menu (Composite Pattern)
 */
 @Override
 public void remove(MenuComponent menuItem) {
 if (menuItems.remove(menuItem)) {
 logger.info("Removed item '" + menuItem.getName() + "' from menu '" + name + "'");
 }
 }

 /**
 * Ottiene un componente figlio per indice (Composite Pattern)
 */
 @Override
 public MenuComponent getChild(int index) {
 if (index >= 0 && index < menuItems.size()) {
 return menuItems.get(index);
 }
 return null;
 }

 /**
 * Ottiene tutti i componenti figli (Composite Pattern)
 */
 @Override
 public List<MenuComponent> getChildren() {
 return new ArrayList<>(menuItems);
 }

 /**
 * Verifica se questo menu ha figli (Composite Pattern)
 */
 @Override
 public boolean hasChildren() {
 return !menuItems.isEmpty();
 }

 /**
 * Ottiene il numero di figli (Composite Pattern)
 */
 @Override
 public int getChildCount() {
 return menuItems.size();
 }

 /**
 * Mostra questo menu e i suoi elementi
 */
 @Override
 public void display() {
 System.out.println("\n=== " + name + " ===");

 for (int i = 0; i < menuItems.size(); i++) {
 System.out.print((i + 1) + ". ");
 menuItems.get(i).display();
 }

 System.out.println("0. Back/Exit");
 }

 /**
 * Display and execute the menu
 */
 @Override
 public void execute() {
 boolean continueMenu = true;

 while (continueMenu) {
 // Display menu
 display();

 // Get user choice
 System.out.print("\nYour choice: ");
 String input = scanner.nextLine();
 Integer choice = InputValidator.validateMenuChoice(input, menuItems.size());

 if (choice == null) {
 continue;
 }

 if (choice == 0) {
 // Exit this menu
 continueMenu = false;
 } else if (choice > 0 && choice <= menuItems.size()) {
 // Execute the selected menu item
 MenuComponent selected = menuItems.get(choice - 1);
 selected.execute();

 // If it was an action (not a submenu), we might want to pause
 if (!(selected instanceof GameMenu)) {
 System.out.println("\nPress Enter to continue...");
 scanner.nextLine();
 }
 }
 }
 }

 /**
 * Ottiene il nome di questo menu
 */
 @Override
 public String getName() {
 return name;
 }

 /**
 * Crea un menu di esempio per test
 */
 public static GameMenu createSampleMenu() {
 GameMenu mainMenu = new GameMenu("Main Menu");

 // Add simple menu items
 mainMenu.add(new MenuItem("Show Character", () -> 
 System.out.println("Character stats would be shown here")));

 mainMenu.add(new MenuItem("Show Inventory", () -> 
 System.out.println("Inventory would be shown here")));

 // Create a submenu
 GameMenu combatMenu = new GameMenu("Combat Options");
 combatMenu.add(new MenuItem("Attack", () -> 
 System.out.println("You attack the enemy!")));
 combatMenu.add(new MenuItem("Defend", () -> 
 System.out.println("You take a defensive stance")));
 combatMenu.add(new MenuItem("Use Potion", () -> 
 System.out.println("You drink a healing potion")));

 // Add submenu to main menu
 mainMenu.add(combatMenu);

 // Add more items to main menu
 mainMenu.add(new MenuItem("Save Game", () -> 
 System.out.println("Game saved successfully")));

 return mainMenu;
 }
}
