import java.util.Random;
import java.util.Scanner;
import java.util.logging.Logger;

/**
 * Dungeon exploration system
 * Implementa il Builder Pattern per costruire l'esperienza di esplorazione
 */
public class DungeonExplorer {

 private static final Logger logger = GameLogger.getLogger();
 private Scanner scanner;
 private Random random;
 private MonsterFactory monsterFactory;
 private CombatSystem combatSystem;

 // Builder per l'esplorazione
 private Character character;
 private Dungeon dungeon;
 private boolean enableRest = true;
 private boolean enableFlee = true;
 private int difficultyModifier = 0;
 private boolean enableLoot = true;
 private int maxRooms = -1; // -1 means use dungeon default

 public DungeonExplorer() {
 try {
 this.scanner = new Scanner(System.in);
 this.random = new Random();
 this.monsterFactory = new MonsterFactory();
 this.combatSystem = new CombatSystem();
 logger.info("DungeonExplorer initialized successfully");
 } catch (Exception e) {
 logger.severe("Failed to initialize DungeonExplorer: " + e.getMessage());
 throw new RuntimeException("Cannot create DungeonExplorer", e);
 }
 }

 /**
 * Builder method: imposta il personaggio
 */
 public DungeonExplorer withCharacter(Character character) {
 this.character = character;
 return this;
 }

 /**
 * Builder method: imposta il dungeon
 */
 public DungeonExplorer withDungeon(Dungeon dungeon) {
 this.dungeon = dungeon;
 return this;
 }

 /**
 * Builder method: abilita/disabilita il riposo tra le stanze
 */
 public DungeonExplorer enableRest(boolean enable) {
 this.enableRest = enable;
 return this;
 }

 /**
 * Builder method: abilita/disabilita la possibilità di fuggire
 */
 public DungeonExplorer enableFlee(boolean enable) {
 this.enableFlee = enable;
 return this;
 }

 /**
 * Builder method: imposta un modificatore di difficoltà
 */
 public DungeonExplorer withDifficultyModifier(int modifier) {
 this.difficultyModifier = modifier;
 return this;
 }

 /**
 * Builder method: abilita/disabilita il bottino
 */
 public DungeonExplorer enableLoot(boolean enable) {
 this.enableLoot = enable;
 return this;
 }

 /**
 * Builder method: imposta il numero massimo di stanze
 */
 public DungeonExplorer withMaxRooms(int maxRooms) {
 this.maxRooms = maxRooms;
 return this;
 }

 /**
 * Builder method: costruisce e avvia l'esplorazione
 * Implementazione del Builder Pattern
 * @return true se l'esplorazione è completata con successo, false altrimenti
 */
 public boolean build() {
 logger.info("Building dungeon exploration experience");

 // Validazione
 if (character == null) {
 logger.warning("Cannot build exploration: missing character");
 System.out.println("Error: No character selected for exploration!");
 return false;
 }

 if (dungeon == null) {
 logger.warning("Cannot build exploration: missing dungeon");
 System.out.println("Error: No dungeon selected for exploration!");
 return false;
 }

 // Determina il numero di stanze
 int roomsToExplore = (maxRooms > 0) ? Math.min(maxRooms, dungeon.getNumberOfRooms()) 
 : dungeon.getNumberOfRooms();

 logger.info("Starting exploration with: " + character.getName() + 
 ", Dungeon: " + dungeon.getName() + 
 ", Rooms: " + roomsToExplore +
 ", Difficulty: " + difficultyModifier);

 // Avvia l'esplorazione
 return exploreDungeon(character, dungeon, roomsToExplore);
 }

 /**
 * Explore a dungeon with specified number of rooms
 */
 private boolean exploreDungeon(Character character, Dungeon dungeon, int roomsToExplore) {
 logger.info("Starting dungeon exploration");

 if (character == null || dungeon == null) {
 logger.warning("Exploration failed: null character or dungeon");
 System.out.println("Error: Missing character or dungeon!");
 return false;
 }

