````java path=Java_Exam_Project/src/main/java/com/exam/project/combat/CombatSystem.java mode=EDIT
package com.exam.project.combat;

import com.exam.project.factory.Character;
import com.exam.project.factoryMonster.AbstractMonster;
import com.exam.project.util.GameLogger;

import java.util.List;
import java.util.Random;
import java.util.Scanner;
import java.util.logging.Logger;

/**
 * Sistema di combattimento semplice
 * Gestisce i combattimenti tra personaggi e mostri
 */
public class CombatSystem {

 private static final Logger logger = GameLogger.getLogger();
 private Random random;
 private Scanner scanner;

 /**
 * Costruttore del sistema di combattimento
 */
 public CombatSystem() {
 try {
 this.random = new Random();
 this.scanner = new Scanner(System.in);
 logger.info("CombatSystem inizializzato");
 } catch (Exception e) {
 logger.severe("Errore nell'inizializzazione del CombatSystem: " + e.getMessage());
 throw new RuntimeException("Impossibile creare CombatSystem", e);
 }
 }

 /**
 * Verifica se un personaggio può combattere (ha abbastanza stamina)
 * @param character Il personaggio da verificare
 * @return true se può combattere, false altrimenti
 */
 public boolean canFight(Character character) {
 try {
 if (character == null) {
 logger.warning("canFight: character is null");
 return false;
 }

 return character.getStamina() >= 3; // Minimo di stamina per attaccare
 } catch (Exception e) {
 logger.warning("Errore in canFight: " + e.getMessage());
 return false;
 }
 }

 /**
 * Verifica se un mostro può combattere
 * @param monster Il mostro da verificare
 * @return true se può combattere, false altrimenti
 */
 public boolean canFight(AbstractMonster monster) {
 try {
 if (monster == null) {
 logger.warning("canFight: monster is null");
 return false;
 }

 return monster.isAlive();
 } catch (Exception e) {
 logger.warning("Errore in canFight (monster): " + e.getMessage());
 return false;
 }
 }

 /**
 * Esegue un attacco del personaggio contro un mostro
 * @param attacker Il personaggio che attacca
 * @param defender Il mostro che difende
 * @return Il danno inflitto
 */
 public int executeAttack(Character attacker, AbstractMonster defender) {
 logger.info("Esecuzione attacco: " + attacker.getName() + " vs " + defender.getName());

 try {
 if (attacker == null || defender == null) {
 logger.warning("executeAttack: attacker or defender is null");
 return 0;
 }

 if (!canFight(attacker)) {
 logger.info("Character cannot fight: not enough stamina");
 System.out.println(attacker.getName() + " è troppo stanco per attaccare!");
 return 0;
 }

 if (!defender.isAlive()) {
 logger.info("Defender is already defeated");
 return 0;
 }

 // Esegui l'attacco
 int damage = attacker.attack();

 // Applica il danno
 if (damage > 0) {
 defender.takeDamage(damage);
 logger.info("Attacco riuscito: " + damage + " danni a " + defender.getName());
 } else {
 logger.info("Attacco fallito o mancato");
 }

 return damage;
 } catch (Exception e) {
 logger.severe("Errore durante l'attacco: " + e.getMessage());
 System.out.println("Si è verificato un errore durante l'attacco!");
 return 0;
 }
 }

 /**
 * Esegue un attacco del mostro contro un personaggio
 * @param attacker Il mostro che attacca
 * @param defender Il personaggio che difende
 * @return Il danno inflitto
 */
 public int executeMonsterAttack(AbstractMonster attacker, Character defender) {
 logger.info("Esecuzione attacco mostro: " + attacker.getName() + " vs " + defender.getName());

 try {
 if (attacker == null || defender == null) {
 logger.warning("executeMonsterAttack: attacker or defender is null");
 return 0;
 }

 if (!attacker.isAlive()) {
 logger.info("Monster cannot attack: not alive");
 return 0;
 }

 if (!defender.isAlive()) {
 logger.info("Defender is already defeated");
 return 0;
 }

 // Esegui l'attacco del mostro
 int damage = attacker.attack();

 // Applica il danno
 if (damage > 0) {
 defender.takeDamage(damage);
 logger.info("Attacco mostro riuscito: " + damage + " danni a " + defender.getName());
 } else {
 logger.info("Attacco mostro fallito o mancato");
 }

 return damage;
 } catch (Exception e) {
 logger.severe("Errore durante l'attacco del mostro: " + e.getMessage());
 System.out.println("Si è verificato un errore durante l'attacco del mostro!");
 return 0;
 }
 }

 /**
 * Gestisce un combattimento completo tra un personaggio e un mostro
 * @param character Il personaggio
 * @param monster Il mostro
 * @return true se il personaggio vince, false se perde
 */
 public boolean fight(Character character, AbstractMonster monster) {
 logger.info("Inizio combattimento: " + character.getName() + " vs " + monster.getName());
 System.out.println("\n=== COMBATTIMENTO ===");
 System.out.println(character.getName() + " vs " + monster.getName());

 try {
 // Continua finché uno dei due non è sconfitto
 while (character.isAlive() && monster.isAlive()) {
 // Mostra stato
 System.out.println("\nVita: " + character.getHealth() + "/" + character.getMaxHealth());
 System.out.println("Stamina: " + character.getStamina() + "/" + character.getMaxStamina());
 System.out.println("Nemico: " + monster.getName() + " - Vita: " + monster.getHealth());

 // Turno del giocatore
 System.out.println("\n1. Attacca");
 System.out.println("2. Tenta la fuga");
 System.out.print("Cosa fai? ");

 int choice;
 try {
 String input = scanner.nextLine();
 choice = Integer.parseInt(input);
 } catch (Exception e) {
 logger.warning("Errore nella lettura dell'input: " + e.getMessage());
 System.out.println("Input non valido! Attacco di default.");
 choice = 1;
 }

 if (choice == 1) {
 // Attacco
 if (canFight(character)) {
 int damage = executeAttack(character, monster);
 if (damage <= 0) {
 System.out.println("Attacco fallito!");
 }
 } else {
 System.out.println("Non hai abbastanza stamina per attaccare!");
 }
 } else if (choice == 2) {
 // Fuga
 if (random.nextInt(100) < 40) {
 System.out.println("Sei riuscito a fuggire!");
 return false;
 } else {
 System.out.println("Fuga fallita!");
 }
 }

 // Verifica se il mostro è stato sconfitto
 if (!monster.isAlive()) {
 System.out.println("\nHai sconfitto " + monster.getName() + "!");
 return true;
 }

 // Turno del mostro
 System.out.println("\n--- Turno del mostro ---");
 int monsterDamage = executeMonsterAttack(monster, character);

 if (monsterDamage <= 0) {
 System.out.println(monster.getName() + " manca l'attacco!");
 }

 // Verifica se il personaggio è stato sconfitto
 if (!character.isAlive()) {
 System.out.println("\nSei stato sconfitto da " + monster.getName() + "!");
 return false;
 }
 }

 // Determina il vincitore
 return character.isAlive();

 } catch (Exception e) {
 logger.severe("Errore critico durante il combattimento: " + e.getMessage());
 System.out.println("Si è verificato un errore durante il combattimento!");
 return false;
 }
 }

 /**
 * Gestisce un combattimento completo tra un personaggio e più mostri
 * @param character Il personaggio
 * @param monsters Lista di mostri
 * @return true se il personaggio vince, false se perde
 */
 public boolean fightMultiple(Character character, List<AbstractMonster> monsters) {
 logger.info("Inizio combattimento multiplo: " + character.getName() + " vs " + monsters.size() + " mostri");
 System.out.println("\n=== COMBATTIMENTO MULTIPLO ===");
 System.out.println(character.getName() + " vs " + monsters.size() + " nemici");

 try {
 // Continua finché il personaggio è vivo e ci sono mostri
 while (character.isAlive() && !monsters.isEmpty()) {
 // Mostra stato
 System.out.println("\nVita: " + character.getHealth() + "/" + character.getMaxHealth());
 System.out.println("Stamina: " + character.getStamina() + "/" + character.getMaxStamina());
 System.out.println("Nemici rimasti: " + monsters.size());

 // Mostra i mostri
 for (int i = 0; i < monsters.size(); i++) {
 AbstractMonster monster = monsters.get(i);
 System.out.println((i+1) + ". " + monster.getName() + " - Vita: " + monster.getHealth());
 }

 // Turno del giocatore
 System.out.println("\n1. Attacca");
 System.out.println("2. Tenta la fuga");
 System.out.print("Cosa fai? ");

 int choice;
 try {
 String input = scanner.nextLine();
 choice = Integer.parseInt(input);
 } catch (Exception e) {
 logger.warning("Errore nella lettura dell'input: " + e.getMessage());
 System.out.println("Input non valido! Attacco di default.");
 choice = 1;
 }

 if (choice == 1) {
 // Attacco - scegli il bersaglio
 if (canFight(character)) {
 System.out.print("Quale nemico vuoi attaccare? (1-" + monsters.size() + "): ");
 int target;
 try {
 String input = scanner.nextLine();
 target = Integer.parseInt(input) - 1;
 if (target < 0 || target >= monsters.size()) {
 System.out.println("Bersaglio non valido! Attacco il primo nemico.");
 target = 0;
 }
 } catch (Exception e) {
 logger.warning("Errore nella scelta del bersaglio: " + e.getMessage());
 System.out.println("Bersaglio non valido! Attacco il primo nemico.");
 target = 0;
 }

 AbstractMonster targetMonster = monsters.get(target);
 int damage = executeAttack(character, targetMonster);

 if (damage <= 0) {
 System.out.println("Attacco fallito!");
 }

 // Rimuovi i mostri sconfitti
 monsters.removeIf(m -> !m.isAlive());
 } else {
 System.out.println("Non hai abbastanza stamina per attaccare!");
 }
 } else if (choice == 2) {
 // Fuga
 if (random.nextInt(100) < 30) { // Più difficile fuggire da gruppi
 System.out.println("Sei riuscito a fuggire!");
 return false;
 } else {
 System.out.println("Fuga fallita!");
 }
 }

 // Verifica se tutti i mostri sono stati sconfitti
 if (monsters.isEmpty()) {
 System.out.println("\nHai sconfitto tutti i nemici!");
 return true;
 }

 // Turno dei mostri
 System.out.println("\n--- Turno dei mostri ---");
 for (AbstractMonster monster : monsters) {
 if (monster.isAlive() && character.isAlive()) {
 int monsterDamage = executeMonsterAttack(monster, character);

 if (monsterDamage <= 0) {
 System.out.println(monster.getName() + " manca l'attacco!");
 }

 // Verifica se il personaggio è stato sconfitto
 if (!character.isAlive()) {
 System.out.println("\nSei stato sconfitto da " + monster.getName() + "!");
 return false;
 }
 }
 }
 }

 // Determina il vincitore
 return character.isAlive();

 } catch (Exception e) {
 logger.severe("Errore critico durante il combattimento multiplo: " + e.getMessage());
 System.out.println("Si è verificato un errore durante il combattimento!");
 return false;
 }
 }
}
