````java path=Java_Exam_Project/src/main/java/com/exam/project/model/Menu.java mode=EDIT
/**
 * Main menu
 */
public static void showMainMenu() {
 boolean exit = false;

 while (!exit) {
 System.out.println("\n=== MENU PRINCIPALE ===");
 System.out.println("1. Crea nuovo personaggio");
 System.out.println("2. Carica personaggio");
 System.out.println("3. Mostra personaggio attuale");
 System.out.println("4. Salva personaggio");
 System.out.println("5. Esplora dungeon"); // Nuova opzione
 System.out.println("0. Esci");
 System.out.print("Scelta: ");

 try {
 int choice = scanner.nextInt();
 scanner.nextLine(); // Consuma newline

 switch (choice) {
 case 1:
 createCharacter();
 break;
 case 2:
 loadCharacter();
 break;
 case 3:
 showCharacter();
 break;
 case 4:
 saveCharacter();
 break;
 case 5:
 exploreDungeon(); // Nuova opzione
 break;
 case 0:
 exit = true;
 System.out.println("Grazie per aver giocato!");
 break;
 default:
 System.out.println("Scelta non valida!");
 }
 } catch (Exception e) {
 logger.severe("Errore nel menu principale: " + e.getMessage());
 System.out.println("Input non valido! Riprova.");
 scanner.nextLine(); // Pulisci l'input
 }
 }
}
