````java path=Java_Exam_Project/src/main/java/com/exam/project/model/GameMenuBuilder.java mode=EDIT
package com.exam.project.model;

import com.exam.project.composite.*;
import com.exam.project.factory.Character;
import com.exam.project.factory.CharacterFactory;
import com.exam.project.factoryMonster.MonsterFactory;
import com.exam.project.combat.CombatSystem;
import com.exam.project.security.InputValidator;
import com.exam.project.util.GameLogger;
import com.exam.project.builder.DungeonFactory;
import com.exam.project.observer.GameUIObserver;
import com.exam.project.io.CharacterManagement;

import java.util.Scanner;
import java.util.List;
import java.util.ArrayList;
import java.util.logging.Logger;

/**
 * Builder per il menu di gioco usando il pattern Composite
 */
public class GameMenuBuilder {

 private static final Logger logger = GameLogger.getLogger();
 private static final Scanner scanner = new Scanner(System.in);
 private static final List<Character> characters = new ArrayList<>();
 private static Character currentCharacter = null;
 private static final GameUIObserver uiObserver = new GameUIObserver();

 /**
 * Costruisce il menu principale del gioco
 */
 public static GameMenu buildMainMenu() {
 try {
 GameMenu mainMenu = new GameMenu("Menu Principale");

 // Aggiungi voci di menu principali
 mainMenu.add(new MenuItem("Crea Personaggio", GameMenuBuilder::createCharacter));
 mainMenu.add(new MenuItem("Seleziona Personaggio", GameMenuBuilder::selectCharacter));
 mainMenu.add(new MenuItem("Mostra Personaggio", GameMenuBuilder::showCharacter));

 // Crea sottomenu Gioca
 GameMenu playMenu = new GameMenu("Gioca");
 playMenu.add(new MenuItem("Esplora Dungeon", GameMenuBuilder::exploreDungeon));
 playMenu.add(new MenuItem("Addestra Personaggio", GameMenuBuilder::trainCharacter));
 mainMenu.add(playMenu);

 // Crea sottomenu Gestione
 GameMenu managementMenu = new GameMenu("Gestione");
 managementMenu.add(new MenuItem("Salva Personaggio", GameMenuBuilder::saveCharacter));
 managementMenu.add(new MenuItem("Carica Personaggio", GameMenuBuilder::loadCharacter));
 managementMenu.add(new MenuItem("Mostra Tutti i Personaggi", GameMenuBuilder::showAllCharacters));
 mainMenu.add(managementMenu);

 // Crea sottomenu Impostazioni
 GameMenu settingsMenu = new GameMenu("Impostazioni");
 settingsMenu.add(new MenuItem("Attiva/Disattiva Notifiche", GameMenuBuilder::toggleNotifications));
 settingsMenu.add(new MenuItem("Informazioni", GameMenuBuilder::showInfo));
 mainMenu.add(settingsMenu);

 return mainMenu;
 } catch (Exception e) {
 logger.severe("Errore nella creazione del menu: " + e.getMessage());
 // Fallback menu di emergenza
 GameMenu emergencyMenu = new GameMenu("Menu");
 emergencyMenu.add(new MenuItem("Esci", () -> {}));
 return emergencyMenu;
 }
 }

 /**
 * Crea un nuovo personaggio
 */
 private static void createCharacter() {
 try {
 System.out.println("\n=== CREA PERSONAGGIO ===");

 CharacterFactory factory = new CharacterFactory();
 factory.showAvailableTypes();

 // Input tipo
 System.out.print("\nTipo (warrior/mage): ");
 String type = InputValidator.sanitizeInput(scanner.nextLine());

 // Input nome
 System.out.print("Nome: ");
 String name = InputValidator.sanitizeInput(scanner.nextLine());

 if (name.isEmpty()) {
 System.out.println("Nome non valido!");
 return;
 }

 // Crea personaggio
 Character character = factory.createCharacter(type, name);

 if (character != null) {
 characters.add(character);
 currentCharacter = character;
 uiObserver.addObservedCharacter(character);
 System.out.println("Personaggio creato: " + character.getName());
 } else {
 System.out.println("Creazione personaggio fallita!");
 }
 } catch (Exception e) {
 logger.warning("Errore nella creazione del personaggio: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Seleziona un personaggio esistente
 */
 private static void selectCharacter() {
 try {
 if (characters.isEmpty()) {
 System.out.println("\nNessun personaggio disponibile!");
 return;
 }

 System.out.println("\n=== SELEZIONA PERSONAGGIO ===");

 // Mostra personaggi
 for (int i = 0; i < characters.size(); i++) {
 Character c = characters.get(i);
 String current = (c == currentCharacter) ? " [ATTUALE]" : "";
 System.out.println((i+1) + ". " + c.getName() + current);
 }

 // Input selezione
 System.out.print("\nNumero: ");
 String input = scanner.nextLine();

 try {
 int choice = Integer.parseInt(input.trim());
 if (choice > 0 && choice <= characters.size()) {
 currentCharacter = characters.get(choice-1);
 System.out.println("Selezionato: " + currentCharacter.getName());
 } else {
 System.out.println("Selezione non valida!");
 }
 } catch (NumberFormatException e) {
 System.out.println("Inserisci un numero valido!");
 }
 } catch (Exception e) {
 logger.warning("Errore nella selezione del personaggio: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Mostra il personaggio attuale
 */
 private static void showCharacter() {
 try {
 if (currentCharacter == null) {
 System.out.println("\nNessun personaggio selezionato!");
 return;
 }

 System.out.println("\n=== PERSONAGGIO ATTUALE ===");
 System.out.println(currentCharacter);
 } catch (Exception e) {
 logger.warning("Errore nella visualizzazione del personaggio: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Esplora un dungeon e combatti
 */
 private static void exploreDungeon() {
 try {
 if (currentCharacter == null) {
 System.out.println("\nSeleziona prima un personaggio!");
 return;
 }

 System.out.println("\n=== ESPLORA DUNGEON ===");

 // Mostra dungeon disponibili
 DungeonFactory.showAvailableDungeons();

 // Scegli dungeon
 System.out.print("\nScegli dungeon (1-2): ");
 String input = scanner.nextLine();
 Integer choice = InputValidator.validateMenuChoice(input, 2);

 if (choice == null || choice < 1) {
 return;
 }

 // Crea dungeon usando il pattern Builder
 var dungeon = DungeonFactory.createDungeonByChoice(choice);
 System.out.println("\nStai esplorando: " + dungeon.getName());

 // Crea mostro
 MonsterFactory monsterFactory = new MonsterFactory();
 String monsterType = (choice == 1) ? "goblin" : "troll";

 System.out.println("\nHai incontrato un " + monsterType + "!");

 // Combatti
 CombatSystem combat = new CombatSystem();
 currentCharacter = combat.doCombat(currentCharacter, 
 monsterFactory.createMonster(monsterType, 1));

 // Notifica UI (Observer pattern)
 uiObserver.notifyObservers("Ha completato l'esplorazione di " + dungeon.getName());
 } catch (Exception e) {
 logger.warning("Errore nell'esplorazione del dungeon: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Addestra il personaggio
 */
 private static void trainCharacter() {
 try {
 if (currentCharacter == null) {
 System.out.println("\nSeleziona prima un personaggio!");
 return;
 }

 System.out.println("\n=== ADDESTRAMENTO ===");
 System.out.println("Statistiche attuali: " + currentCharacter);

 System.out.print("\nProcedere? (s/n): ");
 String input = scanner.nextLine().trim().toLowerCase();

 if (InputValidator.validateYesNo(input)) {
 currentCharacter.train();
 System.out.println("Addestramento completato!");
 System.out.println("Nuove statistiche: " + currentCharacter);

 // Notifica UI (Observer pattern)
 uiObserver.notifyObservers("Ha completato un addestramento");
 }
 } catch (Exception e) {
 logger.warning("Errore nell'addestramento: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Salva il personaggio attuale
 */
 private static void saveCharacter() {
 try {
 if (currentCharacter == null) {
 System.out.println("\nNessun personaggio da salvare!");
 return;
 }

 System.out.println("\n=== SALVA PERSONAGGIO ===");
 System.out.print("Nome file: ");
 String filename = InputValidator.sanitizeFilename(scanner.nextLine());

 if (filename == null || filename.isEmpty()) {
 System.out.println("Nome file non valido!");
 return;
 }

 // Controlla se il file esiste già
 if (CharacterManagement.saveExists(filename)) {
 System.out.print("Il file esiste già. Sovrascrivere? (s/n): ");
 if (!InputValidator.validateYesNo(scanner.nextLine())) {
 System.out.println("Salvataggio annullato.");
 return;
 }
 }

 // Salva il personaggio
 boolean success = CharacterManagement.saveCharacter(currentCharacter, filename);
 if (success) {
 System.out.println("Personaggio salvato con successo!");
 } else {
 System.out.println("Errore nel salvataggio del personaggio.");
 }
 } catch (Exception e) {
 logger.warning("Errore nel salvataggio del personaggio: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Carica un personaggio
 */
 private static void loadCharacter() {
 try {
 System.out.println("\n=== CARICA PERSONAGGIO ===");

 // Ottieni lista dei salvataggi
 String[] saves = CharacterManagement.listSaveFiles();

 if (saves.length == 0) {
 System.out.println("Nessun salvataggio disponibile!");
 return;
 }

 // Mostra salvataggi
 System.out.println("Salvataggi disponibili:");
 for (int i = 0; i < saves.length; i++) {
 System.out.println((i+1) + ". " + saves[i]);
 }

 // Scegli salvataggio
 System.out.print("\nNumero: ");
 String input = scanner.nextLine();
 Integer choice = InputValidator.validateMenuChoice(input, saves.length);

 if (choice == null || choice < 1) {
 return;
 }

 // Carica personaggio
 String filename = saves[choice-1];
 Character loaded = CharacterManagement.loadCharacter(filename);

 if (loaded != null) {
 characters.add(loaded);
 currentCharacter = loaded;
 uiObserver.addObservedCharacter(loaded);
 System.out.println("Personaggio caricato: " + loaded.getName());
 } else {
 System.out.println("Errore nel caricamento del personaggio.");
 }
 } catch (Exception e) {
 logger.warning("Errore nel caricamento del personaggio: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Mostra tutti i personaggi
 */
 private static void showAllCharacters() {
 try {
 System.out.println("\n=== TUTTI I PERSONAGGI ===");

 if (characters.isEmpty()) {
 System.out.println("Nessun personaggio disponibile!");
 return;
 }

 for (int i = 0; i < characters.size(); i++) {
 Character c = characters.get(i);
 String current = (c == currentCharacter) ? " [ATTUALE]" : "";
 System.out.println((i+1) + ". " + c.getName() + current);
 System.out.println(" " + c);
 System.out.println();
 }
 } catch (Exception e) {
 logger.warning("Errore nella visualizzazione dei personaggi: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Attiva/disattiva notifiche
 */
 private static void toggleNotifications() {
 try {
 System.out.println("\n=== IMPOSTAZIONI NOTIFICHE ===");
 System.out.println("1. Attiva notifiche");
 System.out.println("2. Disattiva notifiche");
 System.out.print("Scelta: ");

 String input = scanner.nextLine();
 Integer choice = InputValidator.validateMenuChoice(input, 2);

 if (choice == null) {
 return;
 }

 boolean enable = (choice == 1);
 uiObserver.setShowRecoveryMessages(enable);
 System.out.println("Notifiche " + (enable ? "attivate" : "disattivate") + "!");
 } catch (Exception e) {
 logger.warning("Errore nelle impostazioni notifiche: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Mostra informazioni sul gioco
 */
 private static void showInfo() {
 try {
 System.out.println("\n=== INFORMAZIONI ===");
 System.out.println("RPG Adventure Game");
 System.out.println("Un gioco di ruolo testuale in Java");
 System.out.println("\nPattern di design implementati:");
 System.out.println("- Factory: creazione di personaggi e mostri");
 System.out.println("- Builder: creazione di dungeon");
 System.out.println("- Composite: struttura del menu");
 System.out.println("- Observer: sistema di notifiche");
 System.out.println("- Singleton: logger e altre risorse condivise");
 } catch (Exception e) {
 logger.warning("Errore nella visualizzazione delle informazioni: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Ottieni il personaggio attuale
 */
 public static Character getCurrentCharacter() {
 return currentCharacter;
 }

 /**
 * Ottieni tutti i personaggi
 */
 public static List<Character> getCharacters() {
 return new ArrayList<>(characters);
 }
}
