/**
 * Builder per il menu di gioco usando il pattern Composite
 */
public class GameMenuBuilder {

 private static final Logger logger = GameLogger.getLogger();
 private static final Scanner scanner = new Scanner(System.in);
 private static final List<Character> characters = new ArrayList<>();
 private static Character currentCharacter = null;

 /**
 * Costruisce il menu principale del gioco
 */
 public static GameMenu buildMainMenu() {
 try {
 logger.info("Costruzione del menu principale");
 GameMenu mainMenu = new GameMenu("Menu Principale");

 // Aggiungi voci di menu principali
 mainMenu.add(new MenuItem("Crea Personaggio", GameMenuBuilder::createCharacter));
 mainMenu.add(new MenuItem("Seleziona Personaggio", GameMenuBuilder::selectCharacter));
 mainMenu.add(new MenuItem("Mostra Personaggio", GameMenuBuilder::showCharacter));

 // Crea sottomenu Gioca
 GameMenu playMenu = new GameMenu("Gioca");
 playMenu.add(new MenuItem("Esplora Dungeon", GameMenuBuilder::exploreDungeon));
 playMenu.add(new MenuItem("Addestra Personaggio", GameMenuBuilder::trainCharacter));
 mainMenu.add(playMenu);

 // Crea sottomenu Gestione
 GameMenu managementMenu = new GameMenu("Gestione");
 managementMenu.add(new MenuItem("Mostra Tutti i Personaggi", GameMenuBuilder::showAllCharacters));
 mainMenu.add(managementMenu);

 // Aggiungi voce per uscire
 mainMenu.add(new MenuItem("Esci", () -> System.out.println("Uscita in corso...")));

 logger.info("Menu principale costruito con successo");
 return mainMenu;
 } catch (Exception e) {
 logger.severe("Errore nella creazione del menu: " + e.getMessage());
 // Fallback menu di emergenza
 GameMenu emergencyMenu = new GameMenu("Menu di Emergenza");
 emergencyMenu.add(new MenuItem("Esci", () -> System.out.println("Uscita di emergenza...")));
 return emergencyMenu;
 }
 }

 /**
 * Crea un nuovo personaggio
 */
 private static void createCharacter() {
 try {
 logger.info("Avvio creazione personaggio");
 System.out.println("\n=== CREA PERSONAGGIO ===");

 CharacterFactory factory = new CharacterFactory();
 factory.showAvailableTypes();

 // Input tipo
 System.out.print("\nTipo (warrior/mage): ");
 String type = scanner.nextLine().trim().toLowerCase();

 if (!type.equals("warrior") && !type.equals("mage") && 
 !type.equals("w") && !type.equals("m")) {
 System.out.println("Tipo non valido! Usa 'warrior' o 'mage'.");
 logger.warning("Tentativo di creazione con tipo non valido: " + type);
 return;
 }

 // Input nome
 System.out.print("Nome: ");
 String name = scanner.nextLine().trim();

 if (name.isEmpty()) {
 System.out.println("Nome non valido!");
 logger.warning("Tentativo di creazione con nome vuoto");
 return;
 }

 // Crea personaggio
 Character character = factory.createCharacter(type, name);

 if (character != null) {
 characters.add(character);
 currentCharacter = character;
 System.out.println("Personaggio creato: " + character.getName());
 logger.info("Personaggio creato con successo: " + character.getName());
 } else {
 System.out.println("Creazione personaggio fallita!");
 logger.warning("Creazione personaggio fallita per tipo: " + type + ", nome: " + name);
 }
 } catch (Exception e) {
 logger.warning("Errore nella creazione del personaggio: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Seleziona un personaggio esistente
 */
 private static void selectCharacter() {
 try {
 logger.info("Avvio selezione personaggio");

 if (characters.isEmpty()) {
 System.out.println("\nNessun personaggio disponibile!");
 logger.info("Nessun personaggio disponibile per la selezione");
 return;
 }

 System.out.println("\n=== SELEZIONA PERSONAGGIO ===");

 // Mostra personaggi
 for (int i = 0; i < characters.size(); i++) {
 Character c = characters.get(i);
 String current = (c == currentCharacter) ? " [ATTUALE]" : "";
 System.out.println((i+1) + ". " + c.getName() + current);
 }

 // Input selezione
 System.out.print("\nNumero (0 per annullare): ");
 String input = scanner.nextLine().trim();

 if (input.equals("0")) {
 System.out.println("Selezione annullata.");
 logger.info("Selezione personaggio annullata dall'utente");
 return;
 }

 try {
 int choice = Integer.parseInt(input);
 if (choice > 0 && choice <= characters.size()) {
 currentCharacter = characters.get(choice-1);
 System.out.println("Selezionato: " + currentCharacter.getName());
 logger.info("Personaggio selezionato: " + currentCharacter.getName());
 } else {
 System.out.println("Selezione non valida!");
 logger.warning("Tentativo di selezione con indice non valido: " + choice);
 }
 } catch (NumberFormatException e) {
 System.out.println("Inserisci un numero valido!");
 logger.warning("Input non numerico per selezione personaggio: " + input);
 }
 } catch (Exception e) {
 logger.warning("Errore nella selezione del personaggio: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Mostra il personaggio attuale
 */
 private static void showCharacter() {
 try {
 logger.info("Visualizzazione personaggio attuale");

 if (currentCharacter == null) {
 System.out.println("\nNessun personaggio selezionato!");
 logger.info("Nessun personaggio selezionato per la visualizzazione");
 return;
 }

 System.out.println("\n=== PERSONAGGIO ATTUALE ===");
 System.out.println(currentCharacter);

 // Mostra inventario se disponibile
 try {
 System.out.println("\n--- Inventario ---");
 currentCharacter.showInventory();
 } catch (Exception e) {
 logger.warning("Errore nella visualizzazione dell'inventario: " + e.getMessage());
 System.out.println("Impossibile visualizzare l'inventario.");
 }

 logger.info("Personaggio visualizzato: " + currentCharacter.getName());
 } catch (Exception e) {
 logger.warning("Errore nella visualizzazione del personaggio: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Esplora un dungeon e combatti
 */
 private static void exploreDungeon() {
 try {
 logger.info("Avvio esplorazione dungeon");

 if (currentCharacter == null) {
 System.out.println("\nSeleziona prima un personaggio!");
 logger.info("Tentativo di esplorazione senza personaggio selezionato");
 return;
 }

 if (!currentCharacter.isAlive()) {
 System.out.println("\nIl tuo personaggio è morto! Non può esplorare.");
 logger.info("Tentativo di esplorazione con personaggio morto: " + currentCharacter.getName());
 return;
 }

 System.out.println("\n=== ESPLORA DUNGEON ===");

 // Scegli dungeon
 System.out.println("1. Grotta dei Goblin (Facile)");
 System.out.println("2. Palude dei Troll (Difficile)");
 System.out.print("Scelta (0 per annullare): ");

 String input = scanner.nextLine().trim();

 if (input.equals("0")) {
 System.out.println("Esplorazione annullata.");
 logger.info("Esplorazione annullata dall'utente");
 return;
 }

 int choice;
 try {
 choice = Integer.parseInt(input);
 if (choice < 1 || choice > 2) {
 System.out.println("Scelta non valida!");
 logger.warning("Scelta dungeon non valida: " + choice);
 return;
 }
 } catch (NumberFormatException e) {
 System.out.println("Inserisci un numero valido!");
 logger.warning("Input non numerico per selezione dungeon: " + input);
 return;
 }

 // Crea dungeon
 Dungeon dungeon = new Dungeon();
 if (choice == 1) {
 dungeon.setName("Grotta dei Goblin");
 dungeon.setDescription("Una piccola grotta infestata da goblin.");
 dungeon.setDifficulty(1);
 dungeon.setNumberOfRooms(3);
 } else {
 dungeon.setName("Palude dei Troll");
 dungeon.setDescription("Una palude pericolosa abitata da troll feroci.");
 dungeon.setDifficulty(3);
 dungeon.setNumberOfRooms(5);
 }

 // Conferma
 System.out.println("\nStai per entrare in: " + dungeon.getName());
 System.out.println(dungeon.getDescription());
 System.out.print("Confermi? (s/n): ");
 String confirm = scanner.nextLine().trim().toLowerCase();

 if (!confirm.equals("s")) {
 System.out.println("Esplorazione annullata.");
 logger.info("Esplorazione annullata alla conferma");
 return;
 }

 // Esplora dungeon
 logger.info("Inizio esplorazione: " + dungeon.getName());
 DungeonExplorer explorer = new DungeonExplorer();
 boolean success = explorer.withCharacter(currentCharacter)
 .withDungeon(dungeon)
 .build();

 if (success) {
 System.out.println("\nHai completato l'esplorazione di " + dungeon.getName() + "!");
 logger.info("Esplorazione completata con successo: " + dungeon.getName());
 } else {
 System.out.println("\nNon sei riuscito a completare l'esplorazione.");
 logger.info("Esplorazione fallita: " + dungeon.getName());
 }

 } catch (Exception e) {
 logger.warning("Errore nell'esplorazione del dungeon: " + e.getMessage());
 System.out.println("Si è verificato un errore durante l'esplorazione. Riprova.");
 }
 }

 /**
 * Addestra il personaggio
 */
 private static void trainCharacter() {
 try {
 logger.info("Avvio addestramento personaggio");

 if (currentCharacter == null) {
 System.out.println("\nSeleziona prima un personaggio!");
 logger.info("Tentativo di addestramento senza personaggio selezionato");
 return;
 }

 if (!currentCharacter.isAlive()) {
 System.out.println("\nIl tuo personaggio è morto! Non può addestrarsi.");
 logger.info("Tentativo di addestramento con personaggio morto: " + currentCharacter.getName());
 return;
 }

 System.out.println("\n=== ADDESTRAMENTO ===");
 System.out.println("Statistiche attuali: " + currentCharacter);

 // Verifica stamina
 if (currentCharacter.getStamina() < 10) {
 System.out.println("\nIl personaggio è troppo stanco per addestrarsi!");
 System.out.println("Stamina attuale: " + currentCharacter.getStamina() + "/10 richiesti");
 logger.info("Addestramento fallito per stamina insufficiente: " + currentCharacter.getStamina());
 return;
 }

 System.out.print("\nProcedere con l'addestramento? (s/n): ");
 String input = scanner.nextLine().trim().toLowerCase();

 if (input.equals("s")) {
 currentCharacter.train();
 System.out.println("Addestramento completato!");
 System.out.println("Nuove statistiche: " + currentCharacter);
 logger.info("Addestramento completato per: " + currentCharacter.getName());
 } else {
 System.out.println("Addestramento annullato.");
 logger.info("Addestramento annullato dall'utente");
 }
 } catch (Exception e) {
 logger.warning("Errore nell'addestramento: " + e.getMessage());
 System.out.println("Si è verificato un errore durante l'addestramento. Riprova.");
 }
 }

 /**
 * Mostra tutti i personaggi
 */
 private static void showAllCharacters() {
 try {
 logger.info("Visualizzazione di tutti i personaggi");
 System.out.println("\n=== TUTTI I PERSONAGGI ===");

 if (characters.isEmpty()) {
 System.out.println("Nessun personaggio disponibile!");
 logger.info("Nessun personaggio disponibile per la visualizzazione");
 return;
 }

 for (int i = 0; i < characters.size(); i++) {
 Character c = characters.get(i);
 String current = (c == currentCharacter) ? " [ATTUALE]" : "";
 String status = c.isAlive() ? "Vivo" : "Morto";
 System.out.println((i+1) + ". " + c.getName() + current + " (" + status + ")");
 System.out.println(" " + c);
 System.out.println();
 }

 logger.info("Visualizzati " + characters.size() + " personaggi");
 } catch (Exception e) {
 logger.warning("Errore nella visualizzazione dei personaggi: " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }

 /**
 * Ottieni il personaggio attuale
 */
 public static Character getCurrentCharacter() {
 return currentCharacter;
 }

 /**
