### 3. Correzione del file CombatSystem.java

````java path=Java_Exam_Project/src/main/java/com/exam/project/combat/CombatSystem.java mode=EDIT
package com.exam.project.combat;

import com.exam.project.factory.Character;
import com.exam.project.factoryMonster.AbstractMonster;
import com.exam.project.logger.GameLogger;

import java.util.Scanner;
import java.util.logging.Logger;

/**
 * Sistema di combattimento semplice
 */
public class CombatSystem {

 private static final Logger logger = GameLogger.getLogger();
 private Scanner scanner = new Scanner(System.in);

 /**
 * Esegue un combattimento tra un personaggio e un mostro
 * @param character Il personaggio
 * @param monster Il mostro
 * @return Il personaggio (potenzialmente morto)
 */
 public Character doCombat(Character character, AbstractMonster monster) {
 try {
 if (character == null || monster == null) {
 logger.warning("Tentativo di combattimento con character o monster null");
 return character;
 }

 logger.info("Inizio combattimento: " + character.getName() + " vs " + monster.getName());
 System.out.println("\n=== COMBATTIMENTO ===");
 System.out.println("Nemico: " + monster.getName());

 // Combattimento a turni finché uno dei due non muore
 while (character.isAlive() && monster.isAlive()) {
 // Turno del personaggio
 System.out.println("\nVita: " + character.getHealth() + "/" + character.getMaxHealth());
 System.out.println("Stamina: " + character.getStamina() + "/" + character.getMaxStamina());
 System.out.println("Nemico: " + monster.getName() + " - Vita: " + monster.getHealth());
 System.out.println("\nPremi INVIO per attaccare...");
 scanner.nextLine();

 // Attacco del personaggio
 int damage = character.attack();
 if (damage > 0) {
 monster.takeDamage(damage);
 logger.info("Personaggio attacca per " + damage + " danni");
 } else {
 logger.info("Attacco del personaggio fallito");
 }

 // Verifica se il mostro è morto
 if (!monster.isAlive()) {
 System.out.println("\nHai sconfitto " + monster.getName() + "!");
 logger.info("Mostro sconfitto: " + monster.getName());

 // Ricompensa
 int expReward = monster.getExperienceReward();
 int goldReward = monster.getGoldReward();
 System.out.println("Hai guadagnato " + expReward + " punti esperienza e " + goldReward + " oro!");

 return character;
 }

 // Turno del mostro
 System.out.println("\n" + monster.getName() + " attacca!");
 int monsterDamage = monster.attack();
 if (monsterDamage > 0) {
 character.takeDamage(monsterDamage);
 logger.info("Mostro attacca per " + monsterDamage + " danni");
 } else {
 System.out.println(monster.getName() + " manca il colpo!");
 logger.info("Attacco del mostro fallito");
 }

 // Verifica se il personaggio è morto
 if (!character.isAlive()) {
 System.out.println("\nSei stato sconfitto da " + monster.getName() + "!");
 logger.info("Personaggio sconfitto: " + character.getName());
 return character;
 }
 }

 return character;
 } catch (Exception e) {
 logger.severe("Errore durante il combattimento: " + e.getMessage());
 System.out.println("Si è verificato un errore durante il combattimento!");
 return character;
 }
 }

 /**
 * Verifica se un personaggio può combattere (ha abbastanza stamina)
 */
 public boolean canFight(Character character) {
 try {
 return character != null && character.isAlive() && character.getStamina() >= 3;
 } catch (Exception e) {
 logger.warning("Errore nel controllo canFight: " + e.getMessage());
 return false;
 }
 }

 /**
 * Esegue un attacco da personaggio a mostro
 */
 public int executeAttack(Character character, AbstractMonster monster) {
 try {
 if (character == null || monster == null) {
 return 0;
 }

 int damage = character.attack();
 if (damage > 0) {
 monster.takeDamage(damage);
 }
 return damage;
 } catch (Exception e) {
 logger.warning("Errore nell'esecuzione dell'attacco: " + e.getMessage());
 return 0;
 }
 }

 /**
 * Esegue un attacco da mostro a personaggio
 */
 public int executeMonsterAttack(AbstractMonster monster, Character character) {
 try {
 if (monster == null || character == null) {
 return 0;
 }

 int damage = monster.attack();
 if (damage > 0) {
 character.takeDamage(damage);
 }
 return damage;
 } catch (Exception e) {
 logger.warning("Errore nell'esecuzione dell'attacco del mostro: " + e.getMessage());
 return 0;
 }
 }
}
````

### 4. Correzione del file Dungeon.java

````java path=Java_Exam_Project/src/main/java/com/exam/project/builder/Dungeon.java mode=EDIT
package com.exam.project.builder;

import com.exam.project.logger.GameLogger;
import java.util.logging.Logger;

/**
 * Classe Dungeon - rappresenta un dungeon esplorabile
 * Utilizzata dal pattern Builder
 */
public class Dungeon {
 private static final Logger logger = GameLogger.getLogger();

 private String name;
 private String description;
 private String fullDescription;
 private int difficulty;
 private int numberOfRooms;
 private int goldReward;
 private int experienceReward;

 /**
 * Costruttore con valori predefiniti
 */
 public Dungeon() {
 this.name = "Dungeon sconosciuto";
 this.description = "Un dungeon misterioso";
 this.fullDescription = "Un dungeon misterioso avvolto nell'oscurità";
 this.difficulty = 1;
 this.numberOfRooms = 3;
 this.goldReward = 50;
 this.experienceReward = 100;
 logger.info("Creato nuovo dungeon con valori predefiniti");
 }

 // Getters e setters

 public String getName() {
 return name != null ? name : "Dungeon sconosciuto";
 }

 public void setName(String name) {
 this.name = name;
 logger.info("Impostato nome dungeon: " + name);
 }

 public String getDescription() {
 return description != null ? description : "Nessuna descrizione";
 }

 public void setDescription(String description) {
 this.description = description;
 logger.info("Impostata descrizione dungeon: " + description);
 }

 public String getFullDescription() {
 if (fullDescription != null) {
 return fullDescription;
 }
 return getDescription();
 }

 public void setFullDescription(String fullDescription) {
 this.fullDescription = fullDescription;
 }

 public int getDifficulty() {
 return Math.max(1, difficulty);
 }

 public void setDifficulty(int difficulty) {
 this.difficulty = Math.max(1, difficulty);
 // Aggiorna ricompense in base alla difficoltà
 this.goldReward = 50 * difficulty;
 this.experienceReward = 100 * difficulty;
 logger.info("Impostata difficoltà dungeon: " + difficulty);
 }

 public int getNumberOfRooms() {
 return Math.max(1, numberOfRooms);
 }

 public void setNumberOfRooms(int numberOfRooms) {
 this.numberOfRooms = Math.max(1, numberOfRooms);
 logger.info("Impostato numero stanze: " + numberOfRooms);
 }

 public int getGoldReward() {
 return Math.max(0, goldReward);
 }

 public void setGoldReward(int goldReward) {
 this.goldReward = Math.max(0, goldReward);
 }

 public int getExperienceReward() {
 return Math.max(0, experienceReward);
 }

 public void setExperienceReward(int experienceReward) {
 this.experienceReward = Math.max(0, experienceReward);
 }

 @Override
 public String toString() {
 return String.format("%s (Difficoltà: %d, Stanze: %d)", 
 getName(), getDifficulty(), getNumberOfRooms());
 }
}
````

### 5. Correzione del file GameMenu.java

````java path=Java_Exam_Project/src/main/java/com/exam/project/composite/GameMenu.java mode=EDIT
package com.exam.project.composite;

import com.exam.project.logger.GameLogger;
import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;
import java.util.logging.Logger;

/**
 * Implementazione del pattern Composite per i menu di gioco
 */
public class GameMenu implements MenuComponent {
 private static final Logger logger = GameLogger.getLogger();
 private String name;
 private List<MenuComponent> components;
 private Scanner scanner;

 public GameMenu(String name) {
 this.name = name;
 this.components = new ArrayList<>();
 this.scanner = new Scanner(System.in);
 logger.info("Creato nuovo menu: " + name);
 }

 /**
 * Aggiunge un componente al menu
 */
 public void add(MenuComponent component) {
 if (component != null) {
 components.add(component);
 logger.info("Aggiunto componente al menu " + name);
 }
 }

 /**
 * Rimuove un componente dal menu
 */
 public void remove(MenuComponent component) {
 components.remove(component);
 logger.info("Rimosso componente dal menu " + name);
 }

 /**
 * Esegue il menu, mostrando le opzioni e gestendo l'input
 */
 @Override
 public void execute() {
 boolean exit = false;

 while (!exit) {
 try {
 // Mostra il menu
 System.out.println("\n=== " + name.toUpperCase() + " ===");

 // Mostra le opzioni
 for (int i = 0; i < components.size(); i++) {
 System.out.println((i + 1) + ". " + components.get(i).getName());
 }

 System.out.println("0. Indietro");

 // Ottieni input
 System.out.print("\nScelta: ");
 String input = scanner.nextLine().trim();

 // Gestisci input
 try {
 int choice = Integer.parseInt(input);

 if (choice == 0) {
 exit = true;
 logger.info("Uscita dal menu " + name);
 } else if (choice > 0 && choice <= components.size()) {
 logger.info("Esecuzione opzione " + choice + " del menu " + name);
 components.get(choice - 1).execute();
 } else {
 System.out.println("Scelta non valida!");
 logger.warning("Scelta non valida nel menu " + name + ": " + choice);
 }
 } catch (NumberFormatException e) {
 System.out.println("Inserisci un numero valido!");
 logger.warning("Input non numerico nel menu " + name + ": " + input);
 }
 } catch (Exception e) {
 logger.severe("Errore nell'esecuzione del menu " + name + ": " + e.getMessage());
 System.out.println("Si è verificato un errore. Riprova.");
 }
 }
 }

 /**
 * Restituisce il nome del menu
 */
 @Override
 public String getName() {
 return name;
 }
}
````

