## 1. Semplificazione di `Warrior.java`

<augment_code_snippet path="Java_Exam_Project/src/main/java/com/exam/project/factory/Warrior.java" mode="EDIT">
```java
package com.exam.project.factory;

import com.exam.project.iterator.Item;
import com.exam.project.iterator.Inventory;
import java.util.Random;

/**
 * Warrior character class
 */
public class Warrior extends AbstractCharacter {

 private static final Random random = new Random();
 private final Inventory inventory;

 /**
 * Creates a new Warrior
 * @param name The warrior's name
 */
 public Warrior(String name) {
 super(name, 120, 100, 15);
 this.inventory = new Inventory(25);
 }

 /**
 * Warrior attack
 * @return Damage dealt
 */
 @Override
 public int attack() {
 if (stamina < 5) {
 System.out.printf("%s is too tired to attack!%n", name);
 return 0;
 }

 stamina -= 5;

 int equipmentBonus = inventory != null ? inventory.getTotalStatBonus() : 0;
 int damage = baseDamage + equipmentBonus + random.nextInt(5);

 System.out.printf("%s attacks for %d damage!%n", name, damage);
 return Math.max(1, damage);
 }

 /**
 * Warrior training
 */
 @Override
 protected void performTraining() {
 baseDamage += 2;
 maxHealth += 5;
 health = maxHealth;
 System.out.printf("%s trains with weapons!%n", name);
 }

 /**
 * Equips an item
 * @param item The item to equip
 */
 @Override
 public void equipItem(Item item) {
 if (item == null || inventory == null || !item.isEquippable()) {
 return;
 }

 if (!inventory.getAllItems().contains(item)) {
 System.out.println("Item not in inventory!");
 return;
 }

 inventory.equipItem(item);
 System.out.printf("%s equipped %s!%n", name, item.getName());
 }

 /**
 * Adds an item
 * @param item The item to add
 */
 @Override
 public void addItem(Item item) {
 if (item == null || inventory == null) {
 return;
 }

 if (inventory.addItem(item)) {
 System.out.printf("%s added %s!%n", name, item.getName());
 }
 }

 /**
 * Shows inventory
 */
 @Override
 public void showInventory() {
 if (inventory == null) {
 return;
 }

 System.out.printf("\n=== %s's Equipment ===%n", name);
 inventory.displayInventory();

 int totalBonus = inventory.getTotalStatBonus();
 if (totalBonus > 0) {
 System.out.printf("Equipment bonus: +%d damage%n", totalBonus);
 }
 }

 /**
 * Gets stamina recovery rate
 * @return Recovery rate
 */
 public double getStaminaRecoveryRate() {
 return 0.05;
 }

 /**
 * toString implementation
 */
 @Override
 public String toString() {
 int totalDamage = baseDamage;
 if (inventory != null) {
 totalDamage += inventory.getTotalStatBonus();
 }

 return String.format("Warrior %s [HP: %d/%d, Stamina: %d/%d, Damage: %d, Money: %d, Level: %d]",
 name, health, maxHealth, stamina, maxStamina, totalDamage, money, level);
 }
}
```
</augment_code_snippet>

## 2. Semplificazione di `Mage.java`

<augment_code_snippet path="Java_Exam_Project/src/main/java/com/exam/project/factory/Mage.java" mode="EDIT">
```java
package com.exam.project.factory;

import com.exam.project.iterator.Item;
import com.exam.project.iterator.Inventory;
import java.util.Random;

/**
 * Mage character class
 */
public class Mage extends AbstractCharacter {

 private static final Random random = new Random();
 private int mana;
 private int maxMana;
 private final Inventory inventory;

 /**
 * Creates a new Mage
 * @param name The mage's name
 */
 public Mage(String name) {
 super(name, 80, 120, 10);
 this.mana = 50;
 this.maxMana = 50;
 this.inventory = new Inventory(20);
 }

 /**
 * Mage attack
 * @return Damage dealt
 */
 @Override
 public int attack() {
 if (stamina < 3) {
 System.out.printf("%s is too tired!%n", name);
 return 0;
 }

 stamina -= 3;

 int equipmentBonus = inventory != null ? inventory.getTotalStatBonus() : 0;
 int baseAttack = baseDamage + equipmentBonus;

 // Mana-based attack logic
 if (mana >= 10) {
 mana -= 10;
 int magicDamage = baseAttack + 5 + random.nextInt(10);
 System.out.printf("%s casts a spell for %d damage! Mana: %d/%d%n",
 name, magicDamage, mana, maxMana);
 return magicDamage;
 } else {
 int staffDamage = baseAttack + random.nextInt(3);
 System.out.printf("%s attacks with staff for %d damage!%n", name, staffDamage);
 return staffDamage;
 }
 }

 /**
 * Mage training
 */
 @Override
 protected void performTraining() {
 baseDamage += 1;
 maxMana += 10;
 mana = maxMana;
 maxStamina += 5;
 System.out.printf("%s studies magic!%n", name);
 }

 /**
 * Mage rest
 */
 @Override
 public void rest() {
 super.rest();
 this.mana = maxMana;
 System.out.printf("%s meditates and restores mana!%n", name);
 }

 /**
 * Equips an item
 * @param item The item to equip
 */
 @Override
 public void equipItem(Item item) {
 if (item == null || inventory == null || !item.isEquippable()) {
 return;
 }

 if (!inventory.getAllItems().contains(item)) {
 System.out.println("Item not in inventory!");
 return;
 }

 inventory.equipItem(item);
 System.out.printf("%s equipped %s!%n", name, item.getName());
 }

 /**
 * Adds an item
 * @param item The item to add
 */
 @Override
 public void addItem(Item item) {
 if (item == null || inventory == null) {
 return;
 }

 if (inventory.addItem(item)) {
 System.out.printf("%s added %s!%n", name, item.getName());
 }
 }

 /**
 * Shows inventory
 */
 @Override
 public void showInventory() {
 if (inventory == null) {
 return;
 }

 System.out.printf("\n=== %s's Equipment ===%n", name);
 inventory.displayInventory();

 int totalBonus = inventory.getTotalStatBonus();
 if (totalBonus > 0) {
 System.out.printf("Magic bonus: +%d power%n", totalBonus);
 }
 System.out.printf("Mana: %d/%d%n", mana, maxMana);
 }

 /**
 * Gets stamina recovery rate
 * @return Recovery rate
 */
 public double getStaminaRecoveryRate() {
 return 0.10;
 }

 /**
 * Gets current mana
 * @return Current mana amount
 */
 public int getMana() {
 return mana;
 }

 /**
 * Gets maximum mana
 * @return Maximum mana amount
 */
 public int getMaxMana() {
 return maxMana;
 }

 /**
 * toString implementation
 */
 @Override
 public String toString() {
 int totalDamage = baseDamage;
 if (inventory != null) {
 totalDamage += inventory.getTotalStatBonus();
 }

 return String.format("Mage %s [HP: %d/%d, Stamina: %d/%d, Mana: %d/%d, Power: %d, Money: %d, Level: %d]",
 name, health, maxHealth, stamina, maxStamina,
 mana, maxMana, totalDamage, money, level);
 }
}
```
</augment_code_snippet>

## 3. Semplificazione di `AbstractCharacter.java`

<augment_code_snippet path="Java_Exam_Project/src/main/java/com/exam/project/factory/AbstractCharacter.java" mode="EDIT">
```java
package com.exam.project.factory;

import com.exam.project.iterator.Item;

/**
 * Base class for all characters
 */
public abstract class AbstractCharacter implements Character {

 protected String name;
 protected int health;
 protected int maxHealth;
 protected int stamina;
 protected int maxStamina;
 protected int baseDamage;
 protected int money;
 protected int level;

 /**
 * Constructor
 */
 protected AbstractCharacter(String name, int baseHealth, int baseStamina, int baseDamage) {
 this.name = (name != null && !name.trim().isEmpty()) ? name.trim() : "Unknown";
 this.health = Math.max(1, baseHealth);
 this.maxHealth = this.health;
 this.stamina = Math.max(1, baseStamina);
 this.maxStamina = this.stamina;
 this.baseDamage = Math.max(1, baseDamage);
 this.money = 100;
 this.level = 1;
 }

 /**
 * Takes damage
 * @param damage Amount of damage to take
 */
 @Override
 public void takeDamage(int damage) {
 if (damage < 0) {
 return;
 }

 this.health = Math.max(0, this.health - damage);
 System.out.printf("%s takes %d damage! Health: %d/%d%n",
 name, damage, health, maxHealth);

 if (!isAlive()) {
 System.out.printf("%s has been defeated!%n", name);
 }
 }

 /**
 * Checks if character is alive
 * @return true if health > 0, false otherwise
 */
 @Override
 public boolean isAlive() {
 return health > 0;
 }

 /**
 * Restores stamina
 * @param amount Amount of stamina to restore
 */
 @Override
 public void restoreStamina(int amount) {
 if (amount <= 0) {
 return;
 }

 int oldStamina = stamina;
 stamina = Math.min(maxStamina, stamina + amount);

 int restored = stamina - oldStamina;
 if (restored > 0) {
 System.out.printf("%s restored %d stamina. Stamina: %d/%d%n", 
 name, restored, stamina, maxStamina);
 }
 }

 /**
 * Rest to recover stamina
 */
 @Override
 public void rest() {
 stamina = maxStamina;
 System.out.printf("%s rests and recovers stamina.%n", name);
 }

 /**
 * Train to improve stats
 */
 @Override
 public void train() {
 if (money < 50) {
 System.out.println("Not enough money to train!");
 return;
 }

 money -= 50;
 performTraining();
 level++;

 System.out.printf("%s is now level %d!%n", name, level);
 }

 /**
 * Abstract method for subclass-specific training
 */
 protected abstract void performTraining();

 /**
 * Equip an item
 * @param item The item to equip
 */
 @Override
 public abstract void equipItem(Item item);

 /**
 * Add an item to inventory
 * @param item The item to add
 */
 @Override
 public abstract void addItem(Item item);

 /**
 * Show inventory contents
 */
 @Override
 public abstract void showInventory();

 // Getters
 @Override
 public String getName() {
 return name;
 }

 @Override
 public int getHealth() {
 return health;
 }

 @Override
 public int getMaxHealth() {
 return maxHealth;
 }

 @Override
 public int getStamina() {
 return stamina;
 }

 @Override
 public int getMaxStamina() {
 return maxStamina;
 }

 @Override
 public int getBaseDamage() {
 return baseDamage;
 }

 @Override
 public int getMoney() {
 return money;
 }

 @Override
 public int getLevel() {
 return level;
 }
}
