 1	package com.exam.project.security;
 2	
 3	import com.exam.project.logger.GameLogger;
 4	import java.util.logging.Logger;
 5	
 6	/**
 7	 * Simple input validation
 8	 */
 9	public class InputValidator {
 10	
 11	 private static final Logger logger = GameLogger.getLogger();
 12	
 13	 /**
 14	 * Check character name
 15	 */
 16	 public static String validateCharacterName(String input) {
 17	 logger.info("Validating character name: " + input);
 18	
 19	 if (input == null || input.trim().isEmpty()) {
 20	 logger.warning("Character name validation failed: empty name");
 21	 System.out.println("Name cannot be empty!");
 22	 return null;
 23	 }
 24	
 25	 try {
 26	 String name = input.trim();
 27	
 28	 if (name.length() < 2) {
 29	 logger.warning("Character name validation failed: too short");
 30	 System.out.println("Name too short!");
 31	 return null;
 32	 }
 33	
 34	 if (name.length() > 20) {
 35	 logger.warning("Character name validation failed: too long");
 36	 System.out.println("Name too long!");
 37	 return null;
 38	 }
 39	
 40	 logger.info("Character name validated successfully: " + name);
 41	 return name;
 42	 } catch (Exception e) {
 43	 logger.severe("Error validating character name: " + e.getMessage());
 44	 System.out.println("Name validation error!");
 45	 return null;
 46	 }
 47	 }
 48	
 49	 /**
 50	 * Check menu choice
 51	 */
 52	 public static Integer validateMenuChoice(String input, int max) {
 53	 logger.info("Validating menu choice: " + input + " (max: " + max + ")");
 54	
 55	 if (input == null || input.trim().isEmpty()) {
 56	 logger.warning("Menu choice validation failed: empty input");
 57	 System.out.println("Please enter a number!");
 58	 return null;
 59	 }
 60	
 61	 try {
 62	 int choice = Integer.parseInt(input.trim());
 63	
 64	 if (choice < 0 || choice > max) {
 65	 logger.warning("Menu choice validation failed: out of range (" + choice + ")");
 66	 System.out.println("Choose between 0 and " + max + "!");
 67	 return null;
 68	 }
 69	
 70	 logger.info("Menu choice validated: " + choice);
 71	 return choice;
 72	 } catch (NumberFormatException e) {
 73	 logger.warning("Menu choice validation failed: not a number");
 74	 System.out.println("That's not a number!");
 75	 return null;
 76	 } catch (Exception e) {
 77	 logger.severe("Error validating menu choice: " + e.getMessage());
 78	 System.out.println("Input validation error!");
 79	 return null;
 80	 }
 81	 }
 82	
 83	 /**
 84	 * Check yes/no
 85	 */
 86	 public static boolean validateYesNo(String input) {
 87	 logger.info("Validating yes/no input: " + input);
 88	
 89	 try {
 90	 if (input == null) {
 91	 logger.info("Yes/no validation: null input = false");
 92	 return false;
 93	 }
 94	
 95	 String clean = input.trim().toLowerCase();
 96	 boolean result =
 97	 clean.equals("y") || clean.equals("yes") || clean.equals("s") || clean.equals("si");
 98	
 99	 logger.info("Yes/no validation result: " + result);
 100	 return result;
 101	 } catch (Exception e) {
 102	 logger.warning("Error validating yes/no: " + e.getMessage());
 103	 return false;
 104	 }
 105	 }
 106	
 107	 /**
 108	 * Check filename
 109	 */
 110	 public static String validateFilename(String input) {
 111	 logger.info("Validating filename: " + input);
 112	
 113	 if (input == null || input.trim().isEmpty()) {
 114	 logger.warning("Filename validation failed: empty filename");
 115	 System.out.println("Filename cannot be empty!");
 116	 return null;
 117	 }
 118	
 119	 try {
 120	 String name = input.trim();
 121	
 122	 if (name.length() > 30) {
 123	 logger.warning("Filename validation failed: too long");
 124	 System.out.println("Filename too long!");
 125	 return null;
 126	 }
 127	
 128	 // Sanitize filename
 129	 name = sanitizeFilename(name);
 130	 if (name.isEmpty()) {
 131	 logger.warning("Filename validation failed: invalid characters");
 132	 System.out.println("Invalid filename!");
 133	 return null;
 134	 }
 135	
 136	 String result = name + ".save";
 137	 logger.info("Filename validated: " + result);
 138	 return result;
 139	 } catch (Exception e) {
 140	 logger.severe("Error validating filename: " + e.getMessage());
 141	 System.out.println("Filename validation error!");
 142	 return null;
 143	 }
 144	 }
 145	
 146	 /**
 147	 * Sanitize filename to prevent path traversal attacks
 148	 */
 149	 public static String sanitizeFilename(String input) {
 150	 if (input == null) {
 151	 return "";
 152	 }
 153	
 154	 String name = input.trim();
 155	
 156	 if (name.length() > 30) {
 157	 return "";
 158	 }
 159	
 160	 // Rimuovi caratteri pericolosi e path traversal
 161	 name = name.replaceAll("[^a-zA-Z0-9_\\-]", "_");
 162	
 163	 // Previeni path traversal
 164	 if (name.contains("..")
 165	 || name.startsWith(".")
 166	 || name.startsWith("/")
 167	 || name.startsWith("\\")) {
 168	 return "";
 169	 }
 170	
 171	 return name;
 172	 }
 173	
 174	 public static String sanitizeInput(String string) {
 175	 if (string == null) {
 176	 return "";
 177	 }
 178	 return string.trim();
 179	 }
 180	}...
