 25	 /**
 26	 * Show dungeon menu
 27	 */
 28	 public static void showDungeonMenu(Character character) {
 29	 if (character == null) {
 30	 logger.warning("Cannot show dungeon menu: null character");
 31	 return;
 32	 }
 33	
 34	 if (!character.isAlive()) {
 35	 logger.warning("Cannot show dungeon menu: character is dead");
 36	 System.out.println("Il tuo personaggio è morto! Non puoi esplorare dungeon.");
 37	 throw new ReturnToMainMenuException(); // Torna al menu principale se il personaggio è morto
 38	 }
 39	
 40	 try {
 41	 GameMenu dungeonMenu = buildDungeonMenu(character);
 42	 dungeonMenu.execute();
 43	 } catch (ReturnToMainMenuException e) {
 44	 // Propagate the exception to return to main menu
 45	 throw e;
 46	 } catch (Exception e) {
 47	 logger.severe("Error in dungeon menu: " + e.getMessage());
 48	 System.out.println("Errore nel menu dungeon.");
 49	 }
 50	 }
 51	
