 57	
 58	 menu.add(new MenuItem("Goblin Cave", () -> enterGoblinCave(character)));
 59	 menu.add(new MenuItem("Swamp", () -> enterSwamp(character)));
 60	 menu.add(new MenuItem("Torna al menu personaggio", () -> {
 61	 // Simply exit this menu, will return to character menu
 62	 }));
 63	
 64	 return menu;
 65	 }
