 1	package com.exam.project.composite;
 2	
 3	/**
 4	 * MenuItem - Leaf node in the Composite pattern
 5	 *
 6	 * Represents a single menu item that performs an action
 7	 */
 8	public class MenuItem implements MenuComponent {
 9	 
 10	 private String name;
 11	 private MenuAction action;
 12	 
 13	 /**
 14	 * Constructor for creating a menu item
 15	 */
 16	 public MenuItem(String name, MenuAction action) {
 17	 this.name = name;
 18	 this.action = action;
 19	 }
 20	 
 21	 /**
 22	 * Execute the action associated with this menu item
 23	 */
 24	 @Override
 25	 public void execute() {
 26	 if (action != null) {
 27	 action.execute();
 28	 }
 29	 }
 30	 
 31	 /**
 32	 * Display the menu item
 33	 */
 34	 @Override
 35	 public void display() {
 36	 System.out.println(name);
 37	 }
 38	 
 39	 /**
 40	 * Get the name of this menu item
 41	 */
 42	 @Override
 43	 public String getName() {
 44	 return name;
 45	 }
 46	}
