 44	
 45	 /**
 46	 * Display and execute the menu
 47	 */
 48	 @Override
 49	 public void execute() {
 50	 boolean continueMenu = true;
 51	
 52	 while (continueMenu) {
 53	 // Display menu
 54	 display();
 55	
 56	 // Get user choice
 57	 System.out.print("\nYour choice: ");
 58	 String input = scanner.nextLine();
 59	 
 60	 // Determina il numero massimo di opzioni
 61	 int maxOptions = menuItems.size();
 62	 
 63	 // Controlla se questo menu ha l'opzione 0 visibile
 64	 boolean hasZeroOption = !(title.contains("Menu Personaggio") || 
 65	 title.contains("Esplora Dungeon") || 
 66	 title.contains("Inventario"));
 67	 
 68	 Integer choice = InputValidator.validateMenuChoice(input, maxOptions);
 69	
 70	 if (choice == null) {
 71	 continue;
 72	 }
 73	
 74	 if (choice == 0 && hasZeroOption) {
 75	 // Exit this menu
 76	 continueMenu = false;
 77	 } else if (choice > 0 && choice <= menuItems.size()) {
 78	 // Execute the selected menu item
 79	 MenuComponent selected = menuItems.get(choice - 1);
 80	 selected.execute();
 81	
 82	 // Se l'ultima opzione è "Torna al..." e l'utente l'ha selezionata, esci dal menu
 83	 if (choice == menuItems.size() && 
 84	 selected.getName() != null && 
 85	 (selected.getName().contains("Torna al") || 
 86	 selected.getName().contains("Back to"))) {
 87	 continueMenu = false;
 88	 }
 89	 
 90	 // If it was an action (not a submenu), we might want to pause
 91	 if (!(selected instanceof GameMenu)) {
 92	 System.out.println("\nPress Enter to continue...");
 93	 scanner.nextLine();
 94	 }
 95	 }
 96	 }
 97	 }
 98	
 99	 /**
 100	 * Display the menu options
 101	 */
 102	 @Override
 103	 public void display() {
 104	 System.out.println("\n=== " + title.toUpperCase() + " ===");
 105	
 106	 for (int i = 0; i < menuItems.size(); i++) {
 107	 System.out.print((i + 1) + ". ");
 108	 menuItems.get(i).display();
 109	 }
 110	
 111	 // Mostra l'opzione di uscita appropriata in base al tipo di menu
 112	 if (title.contains("Menu Principale") || title.equals("RPG Adventure Game - Menu Principale")) {
 113	 System.out.println("0. Exit");
 114	 } else if (title.contains("Menu Personaggio") || 
 115	 title.contains("Esplora Dungeon") || 
 116	 title.contains("Inventario")) {
 117	 // Non mostrare l'opzione 0 per questi menu, dato che hanno già opzioni per tornare al menu precedente
 118	 } else {
 119	 System.out.println("0. Indietro");
 120	 }
 121	 }
