 52	 public static Integer validateMenuChoice(String input, int max) {
 53	 logger.info("Validating menu choice: " + input + " (max: " + max + ")");
 54	
 55	 if (input == null || input.trim().isEmpty()) {
 56	 logger.warning("Menu choice validation failed: empty input");
 57	 System.out.println("Please enter a number!");
 58	 return null;
 59	 }
 60	
 61	 try {
 62	 int choice = Integer.parseInt(input.trim());
 63	
 64	 if (choice < 0 || choice > max) {
 65	 logger.warning("Menu choice validation failed: out of range (" + choice + ")");
 66	 System.out.println("Choose between 0 and " + max + "!");
 67	 return null;
 68	 }
 69	
 70	 logger.info("Menu choice validated: " + choice);
 71	 return choice;
 72	 } catch (NumberFormatException e) {
 73	 logger.warning("Menu choice validation failed: not a number");
 74	 System.out.println("That's not a number!");
 75	 return null;
 76	 } catch (Exception e) {
 77	 logger.severe("Error validating menu choice: " + e.getMessage());
 78	 System.out.println("Input validation error!");
 79	 return null;
 80	 }
