 27	 GameMenu mainMenu = new GameMenu("RPG Adventure Game - Menu Principale");
 28	
 29	 mainMenu.add(new MenuItem("Crea nuovo personaggio", MainMenuBuilder::createNewCharacter));
 30	 mainMenu.add(new MenuItem("Carica Personaggio", MainMenuBuilder::loadCharacter));
 31	 mainMenu.add(new MenuItem("Esci", MainMenuBuilder::exitGame));
 32	
 33	 return mainMenu;
 34	 } catch (Exception e) {
 35	 logger.severe("Error building main menu: " + e.getMessage());
 36	 // Emergency fallback
 37	 GameMenu emergency = new GameMenu("Emergency Menu");
 38	 emergency.add(new MenuItem("Esci", () -> System.exit(0)));
 39	 return emergency;
 40	 }
 41	 }
 42	
 43	 /**
 44	 * Create new character and go to character menu
 45	 */
 46	 private static void createNewCharacter() {
 47	 try {
 48	 System.out.println("\n=== CREA NUOVO PERSONAGGIO ===");
 49	
 50	 CharacterFactory factory = new CharacterFactory();
 51	 factory.showAvailableTypes();
 52	
 53	 System.out.print("\nTipo (warrior/mage): ");
 54	 String type = InputValidator.sanitizeInput(scanner.nextLine());
 55	
 56	 System.out.print("Nome: ");
 57	 String name = InputValidator.validateCharacterName(scanner.nextLine());
 58	
 59	 if (name == null) {
 60	 return;
 61	 }
 62	
 63	 Character character = factory.createCharacter(type, name);
 64	
 65	 if (character != null) {
 66	 System.out.println("Personaggio creato: " + character.getName());
 67	 logger.info("Character created: " + character.getName());
 68	
 69	 // Go to character menu
 70	 CharacterMenuBuilder.showCharacterMenu(character);
 71	 } else {
 72	 System.out.println("Creazione fallita!");
 73	 }
 74	 } catch (Exception e) {
 75	 logger.warning("Error creating character: " + e.getMessage());
 76	 System.out.println("Errore nella creazione del personaggio.");
 77	 }
 78	 }
 79	
 80	 /**
 81	 * Load existing character and go to character menu
 82	 */
 83	 private static void loadCharacter() {
 84	 try {
 85	 System.out.println("\n=== CARICA PERSONAGGIO ===");
 86	
 87	 String[] saves = CharacterManagement.listSaveFiles();
 88	 if (saves.length == 0) {
 89	 System.out.println("Nessun salvataggio trovato!");
 90	 return;
 91	 }
 92	
 93	 System.out.println("Salvataggi disponibili:");
 94	 for (int i = 0; i < saves.length; i++) {
 95	 System.out.println((i + 1) + ". " + saves[i]);
 96	 }
 97	
 98	 System.out.print("\nScegli (1-" + saves.length + "): ");
 99	 Integer choice = InputValidator.validateMenuChoice(scanner.nextLine(), saves.length);
 100	
 101	 if (choice != null && choice > 0) {
 102	 Character loaded = CharacterManagement.loadCharacter(saves[choice - 1]);
 103	 if (loaded != null) {
 104	 System.out.println("Personaggio caricato: " + loaded.getName());
 105	
 106	 // Go to character menu
 107	 CharacterMenuBuilder.showCharacterMenu(loaded);
 108	 }
 109	 }
 110	 } catch (Exception e) {
 111	 logger.warning("Error loading character: " + e.getMessage());
 112	 System.out.println("Errore nel caricamento.");
 113	 }
 114	 }
